import React, { useState } from 'react';
import { 
  Typography, Container, Box,
  Button, Dialog, DialogContent, DialogTitle, IconButton, 
  TextField
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CloseIcon from '@mui/icons-material/Close';

function UserProfile() {
  const [openChangePassword, setOpenChangePassword] = useState(false);
  const [openChangeName, setOpenChangeName] = useState(false);
  
  const [passwordForm, setPasswordForm] = useState({
    oldPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const [nameForm, setNameForm] = useState({
    oldName: '',
    newName: ''
  });

  // Các hàm xử lý đóng/mở modal
  const handleChangePasswordOpen = () => setOpenChangePassword(true);
  const handleChangePasswordClose = () => setOpenChangePassword(false);
  const handleChangeNameOpen = () => setOpenChangeName(true);
  const handleChangeNameClose = () => setOpenChangeName(false);

  const handlePasswordChange = (e) => {
    setPasswordForm({
      ...passwordForm,
      [e.target.name]: e.target.value
    });
  };

  const handleNameChange = (e) => {
    setNameForm({
      ...nameForm,
      [e.target.name]: e.target.value
    });
  };

  const handlePasswordSubmit = () => {
    console.log('Đổi mật khẩu:', passwordForm);
    handleChangePasswordClose();
  };

  const handleNameSubmit = () => {
    console.log('Đổi tên:', nameForm);
    handleChangeNameClose();
  };

  return (
    <Container maxWidth="xs" sx={{ paddingY: 4 }}>
      <Button variant="contained" color="primary" onClick={handleChangePasswordOpen}>
        Đổi mật khẩu
      </Button>

      {/* Change Password Modal */}
      <Dialog
        open={openChangePassword}
        onClose={handleChangePasswordClose}
        fullWidth
        maxWidth="xs"
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <IconButton onClick={handleChangePasswordClose}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6">Đổi Mật Khẩu</Typography>
          <IconButton onClick={handleChangePasswordClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ mt: 2 }}>
            <TextField
              fullWidth
              margin="normal"
              label="Mật khẩu cũ"
              type="password"
              name="oldPassword"
              value={passwordForm.oldPassword}
              onChange={handlePasswordChange}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Mật khẩu mới"
              type="password"
              name="newPassword"
              value={passwordForm.newPassword}
              onChange={handlePasswordChange}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Nhập lại mật khẩu mới"
              type="password"
              name="confirmPassword"
              value={passwordForm.confirmPassword}
              onChange={handlePasswordChange}
            />
            <Button
              fullWidth
              variant="contained"
              sx={{ mt: 3 }}
              onClick={handlePasswordSubmit}
            >
              Xác nhận
            </Button>
          </Box>
        </DialogContent>
      </Dialog>

      {/* Change Name Modal */}
      <Dialog
        open={openChangeName}
        onClose={handleChangeNameClose}
        fullWidth
        maxWidth="xs"
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <IconButton onClick={handleChangeNameClose}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6">Đổi Tên</Typography>
          <IconButton onClick={handleChangeNameClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ mt: 2 }}>
            <TextField
              fullWidth
              margin="normal"
              label="Tên hiện tại"
              name="oldName"
              value={nameForm.oldName}
              onChange={handleNameChange}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Tên mới"
              name="newName"
              value={nameForm.newName}
              onChange={handleNameChange}
            />
            <Button
              fullWidth
              variant="contained"
              sx={{ mt: 3 }}
              onClick={handleNameSubmit}
            >
              Xác nhận
            </Button>
          </Box>
        </DialogContent>
      </Dialog>
    </Container>
  );
}

export default UserProfile;
