import React from 'react';
import { Box } from '@mui/material';

const Component1 = () => {
  return (
    <Box
      sx={{
        width: '15%', // Chiều rộng của container
        bgcolor: '#f5f5f5', // Màu nền của container
        display: 'flex',
        flexDirection: 'column', // Sắp xếp các thành phần theo chiều dọc
        alignItems: 'center', // Căn giữa các thành phần theo chiều ngang
        p: 2, // Padding của container
      }}
    >
      {/* Avatar hình tròn */}
      <Box
        sx={{
          width: 50,
          height: 50,
          borderRadius: '50%', // Hình tròn
          bgcolor: '#bbb', // Màu nền của avatar
          mb: 2, // Khoảng cách dưới avatar
        }}
      ></Box>

      {/* Danh sách các ô chữ nhật */}
      <Box
        sx={{
          width: '80%', // Chiều rộng của danh sách
          bgcolor: '#e0e0e0', // Màu nền của container danh sách
          height: '50%', // Chiều cao của danh sách
          mt: 2, // Khoảng cách trên
          p: 1, // Padding bên trong
          display: 'flex',
          flexDirection: 'column', // Sắp xếp các ô theo chiều dọc
          gap: 1, // Khoảng cách giữa các ô
        }}
      >
        {/* Các ô chữ nhật */}
        {[1, 2, 3].map((item) => (
          <Box
            key={item}
            sx={{
              width: '100%', // Chiều rộng ô chữ nhật
              height: 50, // Chiều cao ô chữ nhật
              bgcolor: '#ddd', // Màu nền ô chữ nhật
              borderRadius: 1, // Bo góc của ô
            }}
          ></Box>
        ))}
      </Box>
    </Box>
  );
};

export default Component1;
