import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Paper, IconButton } from '@mui/material';
import { ChevronLeft, ChevronRight } from '@mui/icons-material';
import api from '../utils/api';

const Calendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [tasks, setTasks] = useState([]);

  // Hàm lấy tasks từ API
  const fetchTasks = async () => {
    try {
      // Gọi API cho tất cả các loại task (1-6)
      const allTasks = [];
      for (let i = 1; i <= 6; i++) {
        const response = await api.get(`/tasks/${i}`);
        if (response.data.tasks) {
          allTasks.push(...response.data.tasks);
        }
      }
      setTasks(allTasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [currentMonth, currentYear]);

  // Kiểm tra ngày có task không
  const hasEvents = (day) => {
    const currentDate = new Date(currentYear, currentMonth, day);
    return tasks.some(task => {
      const [startDate] = task.timeStart.split(' '); // "17/12/2024 17:48" -> "17/12/2024"
      const [endDate] = task.timeEnd.split(' '); // "24/12/2024 04:49" -> "24/12/2024"
      
      const [startDay, startMonth, startYear] = startDate.split('/').map(Number);
      const [endDay, endMonth, endYear] = endDate.split('/').map(Number);
      
      const taskStartDate = new Date(startYear, startMonth - 1, startDay);
      const taskEndDate = new Date(endYear, endMonth - 1, endDay);
      
      return currentDate >= taskStartDate && currentDate <= taskEndDate;
    });
  };

  // Lấy tasks cho ngày được chọn
  const getTasksForDate = (day) => {
    const currentDate = new Date(currentYear, currentMonth, day);
    return tasks.filter(task => {
      const [startDate] = task.timeStart.split(' ');
      const [endDate] = task.timeEnd.split(' ');
      
      const [startDay, startMonth, startYear] = startDate.split('/').map(Number);
      const [endDay, endMonth, endYear] = endDate.split('/').map(Number);
      
      const taskStartDate = new Date(startYear, startMonth - 1, startDay);
      const taskEndDate = new Date(endYear, endMonth - 1, endDay);
      
      return currentDate >= taskStartDate && currentDate <= taskEndDate;
    });
  };

  // Lấy màu cho task dựa vào status
  const getTaskColor = (status) => {
    switch (status) {
      case 'COMPLETED':
        return '#4caf50';
      case 'IN_PROGRESS':
        return '#2196f3';
      case 'PENDING':
        return '#ff9800';
      default:
        return '#757575';
    }
  };

  // Các hàm xử lý tháng và tính ngày giữ nguyên
  const handleMonthChange = (direction) => {
    let newMonth = currentMonth + direction;
    let newYear = currentYear;

    if (newMonth < 0) {
      newMonth = 11;
      newYear -= 1;
    } else if (newMonth > 11) {
      newMonth = 0;
      newYear += 1;
    }

    setCurrentMonth(newMonth);
    setCurrentYear(newYear);
    setSelectedDate(null);
  };

  const getDaysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  return (
    <Box sx={{ width: '100%', maxWidth: '500px', margin: '0 auto', padding: 2 }}>
      {/* Header tháng */}
      <Box sx={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 2
      }}>
        <IconButton onClick={() => handleMonthChange(-1)}>
          <ChevronLeft />
        </IconButton>
        <Typography variant="h6" align="center">
          Tháng {currentMonth + 1} {currentYear}
        </Typography>
        <IconButton onClick={() => handleMonthChange(1)}>
          <ChevronRight />
        </IconButton>
      </Box>

      {/* Lưới ngày */}
      <Grid container spacing={1}>
        {/* Header ngày trong tuần */}
        {['T2', 'T3', 'T4', 'T5', 'T6', 'T7', 'CN'].map((day) => (
          <Grid item xs={1.7} key={day}>
            <Typography align="center" color="textSecondary">
              {day}
            </Typography>
          </Grid>
        ))}
        
        {/* Các ngày trong tháng */}
        {Array.from({ length: getDaysInMonth(currentMonth, currentYear) }).map((_, index) => {
          const day = index + 1;
          return (
            <Grid item xs={1.7} key={day}>
              <Box
                onClick={() => setSelectedDate(day)}
                sx={{
                  width: '40px',
                  height: '40px',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  borderRadius: '50%',
                  backgroundColor: day === selectedDate
                    ? '#d32f2f'
                    : hasEvents(day)
                      ? '#e3f2fd'
                      : '#f5f5f5',
                  color: day === selectedDate ? '#fff' : '#000',
                  cursor: 'pointer',
                  position: 'relative',
                  '&::after': hasEvents(day) ? {
                    content: '""',
                    position: 'absolute',
                    bottom: '2px',
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#1976d2'
                  } : {}
                }}
              >
                {day}
              </Box>
            </Grid>
          );
        })}
      </Grid>

      {/* Hiển thị tasks */}
      {selectedDate && (
        <Box sx={{ marginTop: 3 }}>
          <Typography variant="h6">
            Ngày {selectedDate} tháng {currentMonth + 1} năm {currentYear}
          </Typography>
          
          {getTasksForDate(selectedDate).length > 0 ? (
            getTasksForDate(selectedDate).map((task, index) => (
              <Paper
                key={index}
                elevation={2}
                sx={{
                  padding: 2,
                  marginTop: 2,
                  backgroundColor: '#fff',
                  borderRadius: '8px',
                  borderLeft: `4px solid ${getTaskColor(task.status)}`
                }}
              >
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="body1" fontWeight="bold">
                    {task.timeStart.split(' ')[1]} - {task.timeEnd.split(' ')[1]}
                  </Typography>
                  <Typography 
                    variant="caption" 
                    sx={{ 
                      color: getTaskColor(task.status),
                      fontWeight: 'bold',
                      padding: '2px 8px',
                      borderRadius: '12px',
                      backgroundColor: `${getTaskColor(task.status)}15`
                    }}
                  >
                    {task.status === 'PENDING' ? 'Chưa hoàn thành' : 
                     task.status === 'IN_PROGRESS' ? 'Đang thực hiện' : 'Đã hoàn thành'}
                  </Typography>
                </Box>
                <Typography variant="body2" sx={{ mt: 1 }}>
                  {task.task}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {task.description}
                </Typography>
                <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
                  Loại: {task.taskType}
                </Typography>
                <Typography variant="caption" color="text.secondary" sx={{ display: 'block' }}>
                  Tạo bởi: {task.createdBy}
                </Typography>
              </Paper>
            ))
          ) : (
            <Typography sx={{ marginTop: 2 }}>Không có công việc.</Typography>
          )}
        </Box>
      )}
    </Box>
  );
};

export default Calendar;
