import React, { useState, useEffect } from 'react';
import { Box, IconButton, Menu, MenuItem, Dialog, Typography } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import GroupMenu from '../group/GroupMenu';
import UserProfile from '../user_info/user_menu';
import { useNavigate } from 'react-router-dom';
import CreateGroupDialog from './CreateGroupDialog';
import axios from 'axios';

const Component1 = ({ user }) => {
  const navigate = useNavigate();
  const [groups, setGroups] = useState([]);
  const [contextMenu, setContextMenu] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [openGroupMenu, setOpenGroupMenu] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [openCreateGroup, setOpenCreateGroup] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get(
          'http://localhost:3000/api/groups/user-groups',
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        setGroups(response.data.groups);
      } catch (error) {
        console.error('Error fetching groups:', error);
      }
    };

    fetchGroups();
  }, []);

  const handleAddGroup = () => {
    setOpenCreateGroup(true);
  };

  const handleContextMenu = (event, index, group) => {
    event.preventDefault();
    setSelectedIndex(index);
    setSelectedGroup(group);
    setContextMenu({
      mouseX: event.clientX + 2,
      mouseY: event.clientY - 6,
    });
  };

  const handleClose = () => {
    setContextMenu(null);
    setSelectedIndex(null);
    setSelectedGroup(null);
    setIsMenuOpen(false);
  };

  const handleRemoveGroup = async () => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(
        `http://localhost:3000/api/groups/${selectedGroup.group_id}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setGroups((prev) => prev.filter((_, i) => i !== selectedIndex));
      handleClose();
    } catch (error) {
      console.error('Error deleting group:', error);
      alert(error.response?.data?.message || 'Có lỗi xảy ra khi xóa nhóm!');
    }
  };

  const handleGroupClick = (group) => {
    setSelectedGroup(group);
    setOpenGroupMenu(true);
  };

  const handleCloseGroupMenu = () => {
    setOpenGroupMenu(false);
  };

  const handleOpen = () => {
    setIsMenuOpen(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleOpenMainMenu = () => {
    setIsMenuOpen(true);
  };

  const handleGroupCreated = (newGroup) => {
    setGroups(prev => [...prev, newGroup]);
  };

  return (
    <Box
      sx={{
        width: '250px', // Chiều rộng cố định của sidebar
        height: '100vh', // Chiều cao 100% của viewport
        bgcolor: '#f5f5f5', // Màu nền xám nhạt
        display: 'flex',
        flexDirection: 'column', // Sắp xếp các phần tử theo chiều dọc
        alignItems: 'center', // Căn giữa các phần tử theo chiều ngang
        p: 2, // Padding 16px
        border: '1px solid #ccc', // Viền xám nhạt
        borderRadius: 1, // Bo góc nhẹ
      }}
    >
      {/* Avatar hình tròn với ảnh nền */}
      <Box
        sx={{
          width: 100,
          height: 100,
          borderRadius: '50%', // Bo tròn thành hình tròn
          backgroundImage: 'url("/todo.png")',
          backgroundSize: 'cover', // Ảnh phủ kín container
          backgroundPosition: 'center',
          flexShrink: 0, // Không cho phép co lại khi không gian không đủ
          mb: 2, // Margin bottom 16px
        }}
      ></Box>

      {/* Nút + để thêm nhóm */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-start', // Căn trái các phần tử
          width: '100%',
          gap: 1, // Khoảng cách 8px giữa các phần tử
          mb: 2,
        }}
      >
        <IconButton onClick={handleAddGroup} color="primary">
          <AddIcon />
        </IconButton>
      </Box>

      {/* Danh sách các ô group */}
      <Box
        sx={{
          width: '100%',
          bgcolor: '#e0e0e0', // Màu nền xám đậm hơn
          p: 1,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          overflowY: 'auto', // Cho phép cuộn dọc khi nội dung dài
          maxHeight: 'calc(100vh - 150px)', // Chiều cao tối đa
          '&::-webkit-scrollbar': {
            width: '8px', // Độ rộng của thanh cuộn
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#888', // Màu của thanh cuộn
            borderRadius: '4px', // Bo góc thanh cuộn
          },
          '&::-webkit-scrollbar-thumb:hover': {
            backgroundColor: '#555', // Màu khi hover thanh cuộn
          },
        }}
      >
        {groups.map((group, index) => (
          <Box
            key={group.group_id}
            onClick={() => handleGroupClick(group)}
            onContextMenu={(e) => handleContextMenu(e, index, group)}
            sx={{
              width: '100%',
              height: 65,
              bgcolor: '#fff',
              borderRadius: 1,
              flexShrink: 0,
              cursor: 'pointer',
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              '&:hover': {
                bgcolor: '#f5f5f5',
              },
            }}
          >
            <Typography variant="subtitle1" noWrap>
              {group.group_name}
            </Typography>
            <Typography variant="caption" color="textSecondary" noWrap>
              {group.role === 'leader' ? 'Trưởng nhóm' : 'Thành viên'}
            </Typography>
          </Box>
        ))}
      </Box>

      {/* Dialog hiển thị GroupMenu */}
      <Dialog
        open={openGroupMenu}
        onClose={handleCloseGroupMenu}
        maxWidth={false}
        PaperProps={{
          sx: {
            width: '90vw',
            height: '90vh',
            maxWidth: 'none',
            maxHeight: 'none'
          }
        }}
      >
        <GroupMenu group={selectedGroup} />
      </Dialog>

      {/* Menu ngữ cảnh với vị trí dựa trên tọa độ chuột */}
      <Menu
        open={contextMenu !== null}
        onClose={handleClose}
        anchorReference="anchorPosition"
        anchorPosition={
          contextMenu !== null
            ? { top: contextMenu.mouseY, left: contextMenu.mouseX }
            : undefined
        }
      >
        {selectedGroup?.role === 'leader' && (
          <MenuItem onClick={handleRemoveGroup}>Xóa nhóm</MenuItem>
        )}
        <MenuItem onClick={handleClose}>Hủy</MenuItem>
      </Menu>

      <UserProfile 
        open={isMenuOpen}
        onClose={handleClose}
        onLogout={handleLogout}
        openMainMenu={handleOpenMainMenu}
        user={user}
      />

      <CreateGroupDialog
        open={openCreateGroup}
        onClose={() => setOpenCreateGroup(false)}
        onSuccess={handleGroupCreated}
      />
    </Box>
  );
};

export default Component1;
