import React, { useState } from 'react';
import { Box, Avatar, IconButton } from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import Component1 from './menu/Component1';
import Component2 from './menu/Component2';
import Component3 from './menu/Component3';
import Component4 from './menu/Component4';
import UserProfile from '../user_info/user_menu';

const MenuLayout = () => {
  const [openUserMenu, setOpenUserMenu] = useState(false);

  return (
    <Box
      sx={{
        display: 'flex',
        height: '100vh',
        width: '100vw',
        p: 1.5,
        borderRadius: '16px',
        overflow: 'hidden',
      }}
    >
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* Logo phía trên Lịch với borderRadius */}
        <Box
          sx={{
            width: '80%',
            margin: '0 auto 40px',
            height: 80,
            backgroundImage: 'url(/logo.png)',
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            borderRadius: '16px',
            overflow: 'hidden',
          }}
        />

        {/* Component 2 - Lịch */}
        <Component2 />

        {/* Component 4 - Thanh tìm kiếm */}
        <Component4 />

        {/* Component 3 */}
        <Component3 />
      </Box>

      {/* Avatar và nút chuông */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          right: 30,
          display: 'flex',
          alignItems: 'center',
          gap: 1.75,
          zIndex: 20,
        }}
      >
        {/* Nút hình chuông */}
        <IconButton
          aria-label="notifications"
          sx={{
            width: 60,
            height: 60,
            bgcolor: 'transparent',
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.1)',
            },
          }}
        >
          <NotificationsIcon sx={{ fontSize: 30 }} />
        </IconButton>

        {/* Avatar với onClick */}
        <IconButton
          onClick={() => setOpenUserMenu(true)}
          sx={{
            p: 0,
            '&:hover': {
              opacity: 0.8,
            },
          }}
        >
          <Avatar
            alt="User Avatar"
            src="/path/to/avatar.jpg"
            sx={{ width: 57, height: 57 }}
          />
        </IconButton>
      </Box>

      {/* Thêm UserProfile component */}
      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
      />
    </Box>
  );
};

export default MenuLayout;
