import React, { useState } from 'react';
import { Box, Avatar, IconButton } from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications'; // Import icon hình chuông
import Component1 from './menu/Component1';
import Component2 from './menu/Component2';
import Component3 from './menu/Component3';
import Component4 from './menu/Component4';
import UserProfile from './UserProfile';

// import Component1 from './Component1';
// import Component2 from './Component2';
// import Component3 from './Component3';
// import Component4 from './Component4'; 
// để như này khi ma layout ở ngoài


const MenuLayout = () => {
  const [openUserMenu, setOpenUserMenu] = useState(false);

  const handleAvatarClick = () => {
    setOpenUserMenu(true);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        height: '100vh',
        width: '100vw',
        p: 1.5,
        borderRadius: '16px', // Bo góc cho toàn bộ layout
        overflow: 'hidden', // Đảm bảo phần tử con không vượt ra ngoài góc bo
      }}
    >
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ 
        flex: 1, 
        display: 'flex', 
        flexDirection: 'column',
        paddingLeft: '240px', // Thêm padding để dịch sang phải
      }}>
        {/* Logo phía trên Lịch với borderRadius */}
        <Box
          sx={{
            width: '80%',
            margin: '0 auto 40px',
            height: 80,
            backgroundImage: 'url(/logo.png)', // Đường dẫn logo
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            borderRadius: '16px', // Bo góc cho ảnh logo
            overflow: 'hidden', // Đảm bảo rằng không có phần nào vượt ra ngoài góc bo
          }}
        />

        {/* Component 2 - Lịch */}
        <Component2 />

        {/* Component 4 - Thanh tìm kiếm */}
        <Component4 />

        {/* Component 3 */}
        <Component3 />
      </Box>

      {/* Avatar và nút chuông */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          right: 30,
          display: 'flex',
          alignItems: 'center',
          gap: 1.75 , // Khoảng cách giữa nút chuông và avatar
          zIndex: 20, // Đảm bảo nó nằm trên các phần tử khác
          marginLeft: 'auto', // Đảm bảo nút luôn nằm bên phải
        }}
      >
        {/* Nút hình chuông */}
        <IconButton
          aria-label="notifications"
          sx={{
            width: 60,
            height: 60, // Kích thước nút chuông giống avatar
            bgcolor: 'transparent', // Nền trong suốt
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.1)', // Hiệu ứng hover
            },
          }}
        >
          <NotificationsIcon sx={{ fontSize: 30 }} /> {/* Kích thước icon chuông */}
        </IconButton>

        {/* Avatar */}
        <Avatar
          alt="User Avatar"
          src="/path/to/avatar.jpg" // Đường dẫn ảnh avatar
          sx={{ width: 57, height: 57 }} // Kích thước avatar
        />
      </Box>

      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
      />
    </Box>
  );
};

export default MenuLayout;
