import React from 'react';
import { Box, Typography } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'; // Import biểu tượng lịch

const Component2 = () => {
  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 2, p: 1 }}>
      {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
        <Box
          key={day}
          sx={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            bgcolor: '#f5f5f5',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            position: 'relative', // Đặt position để kiểm soát biểu tượng
          }}
        >
          <Typography>{day}</Typography>
          {day === 'Sun' && (
            <Box
              sx={{
                position: 'absolute',
                top: -10,
                right: -10,
                width: 16,
                height: 16,
                bgcolor: '#1976d2',
                color: '#fff',
                borderRadius: '50%',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <CalendarMonthIcon sx={{ fontSize: 12 }} />
            </Box>
          )}
        </Box>
      ))}
    </Box>
  );
};

export default Component2;
