import React, { useState } from 'react';
import { Box, Grid, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import TaskList from './TaskList'; // Import TaskList Component

const Component3 = () => {
  const items = ['Học Tập', 'Công Việc', 'Gia Đình', 'Hàng Ngày', 'Hàng Tháng', 'Hàng Năm'];

  // State để điều khiển việc mở/đóng Dialog
  const [open, setOpen] = useState(false);

  // Hàm mở dialog
  const handleClickOpen = () => {
    setOpen(true);
  };

  // Hàm đóng dialog
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ 
      flex: 1, // Chiếm hết không gian còn lại
      pl: '0px' // Padding left 10px
    }}>
      <Box
        sx={{
          width: '70%', // Chiều rộng 70% của container
          margin: '0 auto 16px', // Căn giữa và margin bottom 16px
          height: 80, // Chiều cao cố định
          backgroundImage: 'url(/path/to/logo.png)',
          backgroundSize: 'contain', // Giữ tỷ lệ ảnh gốc
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
        }}
      />

      <Grid
        container
        spacing={3}
        sx={{
          flex: 1,
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={4} sm={4} key={index}>
            <Box
              onClick={handleClickOpen}
              sx={{
                width: '100%',
                height: 150,
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 24,
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
              }}
            >
              {item}
            </Box>
          </Grid>
        ))}
      </Grid>

      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth={false} // Tắt giới hạn chiều rộng mặc định
      >
        <DialogTitle>Danh Sách Nhiệm Vụ</DialogTitle>
        <DialogContent
          sx={{
            width: 900, // Chiều rộng cố định của Dialog
            height: 600, // Chiều cao cố định của Dialog
            overflowY: 'auto', // Cho phép cuộn dọc khi nội dung vượt quá
          }}
        >
          <TaskList />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Đóng</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Component3;
