import React, { useState } from "react";
import {
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TableContainer,
  Paper,
  Checkbox,
  Button,
  TablePagination,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Typography,
} from "@mui/material";
import SubmitTask from "./SubmitTask"; // import SubmitTask component

const TaskList = () => {
  const [tasks, setTasks] = useState([
    // Dữ liệu các task
  ]);

  const [selectedTasks, setSelectedTasks] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(7);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [openSubmitTask, setOpenSubmitTask] = useState(false); // State để mở SubmitTask

  const handleOpenSubmitTask = () => {
    setOpenSubmitTask(true);
  };

  const handleCloseSubmitTask = () => {
    setOpenSubmitTask(false);
  };

  const handleToggleSelect = (id) => {
    // Logic chọn task
  };

  const handleDelete = () => {
    // Logic xóa task
  };

  const handleAdd = () => {
    // Logic thêm task
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleOpenDialog = (task) => {
    setSelectedTask(task);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedTask(null);
  };

  const handleEditTask = () => {
    setOpenDialog(false);
    handleOpenSubmitTask();
  };

  const displayedTasks = tasks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  return (
    <TableContainer component={Paper} style={{ marginTop: "20px", maxWidth: "90%", margin: "auto" }}>
      {/* Nội dung bảng công việc */}
      <Table>
        <TableHead>
          <TableRow>
            {/* Các tiêu đề */}
          </TableRow>
        </TableHead>
        <TableBody>
          {displayedTasks.map((task) => (
            <TableRow key={task.id}>
              {/* Các ô chứa nội dung */}
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <TablePagination
        rowsPerPageOptions={[7, 10, 20]}
        component="div"
        count={tasks.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />

      {/* Dialog hiển thị thông tin */}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Task Details</DialogTitle>
        <DialogContent>
          {selectedTask ? (
            <>
              <Typography variant="h6">{selectedTask.task}</Typography>
              {/* Thông tin khác của task */}
            </>
          ) : (
            <Typography>No Task Selected</Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditTask} variant="contained" color="primary">
            Edit
          </Button>
          <Button onClick={handleCloseDialog} variant="contained" color="secondary">
            Close
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog SubmitTask */}
      <Dialog open={openSubmitTask} onClose={handleCloseSubmitTask} maxWidth="sm" fullWidth>
        <SubmitTask task={selectedTask} onClose={handleCloseSubmitTask} />
      </Dialog>
    </TableContainer>
  );
};

export default TaskList;
