import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Paper, IconButton } from '@mui/material';
import { ChevronLeft, ChevronRight } from '@mui/icons-material';
import api from '../utils/api';

const Calendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [tasks, setTasks] = useState([]);

  // Lấy dữ liệu tasks
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await api.get('/tasks/1'); // Lấy tasks loại học tập
        console.log("Tasks data:", response.data.tasks); // Debug
        setTasks(response.data.tasks || []);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };
    fetchTasks();
  }, []);

  // Kiểm tra ngày có task không
  const checkDateHasTask = (day) => {
    const checkDate = new Date(currentYear, currentMonth, day);
    
    return tasks.some(task => {
      // Chuyển đổi timeStart từ format "DD/MM/YYYY HH:mm"
      const [startDateStr] = task.timeStart.split(' ');
      const [startDay, startMonth, startYear] = startDateStr.split('/');
      const startDate = new Date(startYear, startMonth - 1, startDay);

      // Chuyển đổi timeEnd từ format "DD/MM/YYYY HH:mm"
      const [endDateStr] = task.timeEnd.split(' ');
      const [endDay, endMonth, endYear] = endDateStr.split('/');
      const endDate = new Date(endYear, endMonth - 1, endDay);

      return checkDate >= startDate && checkDate <= endDate;
    });
  };

  // Lấy tasks cho ngày được chọn
  const getTasksForDate = (day) => {
    const selectedDate = new Date(currentYear, currentMonth, day);
    
    return tasks.filter(task => {
      const [startDateStr] = task.timeStart.split(' ');
      const [startDay, startMonth, startYear] = startDateStr.split('/');
      const startDate = new Date(startYear, startMonth - 1, startDay);

      const [endDateStr] = task.timeEnd.split(' ');
      const [endDay, endMonth, endYear] = endDateStr.split('/');
      const endDate = new Date(endYear, endMonth - 1, endDay);

      return selectedDate >= startDate && selectedDate <= endDate;
    });
  };

  // Xử lý chuyển tháng
  const handleMonthChange = (direction) => {
    let newMonth = currentMonth + direction;
    let newYear = currentYear;

    if (newMonth > 11) {
      newMonth = 0;
      newYear += 1;
    } else if (newMonth < 0) {
      newMonth = 11;
      newYear -= 1;
    }

    setCurrentMonth(newMonth);
    setCurrentYear(newYear);
  };

  // Lấy số ngày trong tháng
  const getDaysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header tháng */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 3 }}>
        <IconButton onClick={() => handleMonthChange(-1)}>
          <ChevronLeft />
        </IconButton>
        <Typography variant="h5" sx={{ mx: 2 }}>
          Tháng {currentMonth + 1} năm {currentYear}
        </Typography>
        <IconButton onClick={() => handleMonthChange(1)}>
          <ChevronRight />
        </IconButton>
      </Box>

      {/* Lưới ngày */}
      <Grid container spacing={1}>
        {/* Header ngày trong tuần */}
        {['T2', 'T3', 'T4', 'T5', 'T6', 'T7', 'CN'].map(day => (
          <Grid item xs={1.7} key={day}>
            <Box sx={{ textAlign: 'center', fontWeight: 'bold' }}>
              {day}
            </Box>
          </Grid>
        ))}

        {/* Các ngày trong tháng */}
        {Array.from({ length: getDaysInMonth(currentMonth, currentYear) }).map((_, index) => {
          const day = index + 1;
          const hasTask = checkDateHasTask(day);

          return (
            <Grid item xs={1.7} key={day}>
              <Box
                onClick={() => setSelectedDate(day)}
                sx={{
                  height: 40,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  borderRadius: '50%',
                  bgcolor: hasTask ? '#bbdefb' : 
                           selectedDate === day ? '#f44336' : '#f5f5f5',
                  color: selectedDate === day ? 'white' : 'inherit',
                  '&:hover': {
                    bgcolor: hasTask ? '#90caf9' : '#e0e0e0'
                  }
                }}
              >
                {day}
              </Box>
            </Grid>
          );
        })}
      </Grid>

      {/* Hiển thị tasks của ngày được chọn */}
      {selectedDate && (
        <Box sx={{ mt: 3 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>
            Công việc ngày {selectedDate}/{currentMonth + 1}/{currentYear}
          </Typography>
          
          {getTasksForDate(selectedDate).map((task, index) => (
            <Paper
              key={index}
              sx={{
                p: 2,
                mb: 2,
                borderLeft: `4px solid ${
                  task.status === 'PENDING' ? '#ff9800' :
                  task.status === 'IN_PROGRESS' ? '#2196f3' : '#4caf50'
                }`
              }}
            >
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography variant="subtitle1" fontWeight="bold">
                  {task.task}
                </Typography>
                <Typography
                  variant="caption"
                  sx={{
                    px: 1,
                    py: 0.5,
                    borderRadius: 1,
                    bgcolor: task.status === 'PENDING' ? '#fff3e0' :
                            task.status === 'IN_PROGRESS' ? '#e3f2fd' : '#e8f5e9',
                    color: task.status === 'PENDING' ? '#f57c00' :
                           task.status === 'IN_PROGRESS' ? '#1976d2' : '#2e7d32'
                  }}
                >
                  {task.status === 'PENDING' ? 'Chưa hoàn thành' :
                   task.status === 'IN_PROGRESS' ? 'Đang thực hiện' : 'Hoàn thành'}
                </Typography>
              </Box>
              
              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                {task.timeStart.split(' ')[1]} - {task.timeEnd.split(' ')[1]}
              </Typography>
              
              <Typography variant="body2" sx={{ mb: 1 }}>
                {task.description}
              </Typography>
              
              <Typography variant="caption" color="text.secondary" display="block">
                Loại: {task.taskType}
              </Typography>
              
              <Typography variant="caption" color="text.secondary">
                Người tạo: {task.createdBy}
              </Typography>
            </Paper>
          ))}
          
          {getTasksForDate(selectedDate).length === 0 && (
            <Typography color="text.secondary">
              Không có công việc nào.
            </Typography>
          )}
        </Box>
      )}
    </Box>
  );
};

export default Calendar;
