import React from 'react';
import { Box, Avatar } from '@mui/material';
import Component1 from './menu/Component1';
import Component2 from './menu/Component2';
import Component3 from './menu/Component3';
import Component4 from './menu/Component4';

const MenuLayout = () => {
  return (
    <Box sx={{ display: 'flex', height: '200vh', width: '100vw', p: 2 }}>
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* Component 2 - Lịch */}
        <Component2 />

        {/* Component 4 - Thanh tìm kiếm */}
        <Component4 />

        <Component3 />
      </Box>

      {/* Avatar in top-right corner */}
      <Box
        sx={{
          position: 'absolute',
          top: 16,
          right: 16,
          zIndex: 10, // Ensure it's above other components
        }}
      >
        <Avatar alt="User Avatar" src="/path/to/avatar.jpg" />
      </Box>
    </Box>
  );
};

export default MenuLayout;
