import React from 'react';
import { Box, Typography } from '@mui/material';

const Component3 = () => {
  const items = [
    { title: 'Học Tập', color: '#2196f3' },
    { title: 'Công Việc', color: '#2196f3' },
    { title: 'Gia Đình', color: '#2196f3' },
    { title: 'Hàng Ngày', color: '#2196f3' },
    { title: 'Hàng Tháng', color: '#2196f3' },
    { title: 'Hàng Năm', color: '#2196f3' },
  ];

  return (
    <Box
      sx={{
        display: 'grid',
        gridTemplateColumns: 'repeat(3, 1fr)', // 3 cột bằng nhau
        gap: '20px', // Khoảng cách giữa các ô
        padding: '20px',
        width: '95%', // Tăng độ rộng của container
        margin: '0 auto', // Căn giữa container
      }}
    >
      {items.map((item, index) => (
        <Box
          key={index}
          sx={{
            backgroundColor: '#e0e0e0',
            borderRadius: '16px',
            padding: '30px', // Tăng padding để ô to hơn
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: '150px', // Tăng chiều cao tối thiểu
            cursor: 'pointer',
            transition: 'transform 0.2s, box-shadow 0.2s',
            '&:hover': {
              transform: 'scale(1.02)',
              boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            },
          }}
        >
          <Typography
            variant="h5"
            sx={{
              color: item.color,
              fontWeight: 'bold',
              textAlign: 'center',
            }}
          >
            {item.title}
          </Typography>
        </Box>
      ))}
    </Box>
  );
};

export default Component3;
