import React, { useState } from "react";
import {
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TableContainer,
  Paper,
  Checkbox,
  Button,
  TablePagination,
} from "@mui/material";

const TaskList = () => {
  const [tasks, setTasks] = useState([
    { id: 1, task: "Project 1 (Chung) - HK 2024.1", status: "RUNNING", createdBy: "admin", createdAt: "2024-09-22 07:33:10" },
    { id: 2, task: "C_BASIC_STACK", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-10-26 05:56:49" },
    { id: 3, task: "C Basic Final Exam Kip 2 20231", status: "COMPLETED", createdBy: "hieu.nguyenkiem@hust.edu.vn", createdAt: "2024-01-15 20:17:11" },
    { id: 4, task: "CBasic_NVTung_20231_guixe", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2024-01-11 11:25:54" },
    { id: 5, task: "CBasic_NVTung_20231 Sort", status: "RUNNING", createdBy: "tung.nguyenviet", createdAt: "2023-12-14 05:19:57" },
    { id: 6, task: "CBasic_NVTung_20231 tree", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-12-14 05:04:49" },
    { id: 7, task: "CBasic_NVTung_20231 BST", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-12-07 04:58:00" },
    { id: 8, task: "CBasic_NVTung_20231 Hash", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-12-28 05:14:53" },
    { id: 9, task: "CBasic_NVTung_20231 Final", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-11-30 09:31:46" },
  ]);

  const [selectedTasks, setSelectedTasks] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(7); // Hiển thị tối đa 7 dòng mỗi trang

  const handleToggleSelect = (id) => {
    setSelectedTasks((prevSelected) =>
      prevSelected.includes(id)
        ? prevSelected.filter((taskId) => taskId !== id)
        : [...prevSelected, id]
    );
  };

  const handleDelete = () => {
    setTasks((prevTasks) => prevTasks.filter((task) => !selectedTasks.includes(task.id)));
    setSelectedTasks([]);
  };

  const handleAdd = () => {
    const newTask = {
      id: tasks.length + 1,
      task: `New Task ${tasks.length + 1}`,
      status: "PENDING",
      createdBy: "admin",
      createdAt: new Date().toISOString().slice(0, 19).replace("T", " "),
    };
    setTasks((prevTasks) => [...prevTasks, newTask]);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const displayedTasks = tasks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  return (
    <TableContainer component={Paper} style={{ marginTop: "20px", maxWidth: "90%", margin: "auto" }}>
      {/* Tiêu đề */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", margin: "20px 0" }}>
        <h2 style={{ textAlign: "center", flex: 1 }}>Danh Sách Công Việc</h2>
        <div>
          <Button
            variant="contained"
            color="primary"
            style={{ marginRight: "10px" }}
            onClick={handleAdd}
          >
            +
          </Button>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleDelete}
            disabled={selectedTasks.length === 0}
          >
            -
          </Button>
        </div>
      </div>

      {/* Bảng danh sách công việc */}
      <Table>
        <TableHead>
          <TableRow>
            <TableCell />
            <TableCell style={{ fontWeight: "bold" }}>Task</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Status</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Created By</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Created At</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {displayedTasks.map((task) => (
            <TableRow key={task.id}>
              <TableCell>
                <Checkbox
                  checked={selectedTasks.includes(task.id)}
                  onChange={() => handleToggleSelect(task.id)}
                />
              </TableCell>
              <TableCell>
                <a href="#">{task.task}</a>
              </TableCell>
              <TableCell>{task.status}</TableCell>
              <TableCell>{task.createdBy}</TableCell>
              <TableCell>{task.createdAt}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      {/* Phân trang */}
      <TablePagination
        rowsPerPageOptions={[7, 10, 20]}
        component="div"
        count={tasks.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </TableContainer>
  );
};

export default TaskList;
