import React, { useState } from 'react';
import { Box, Typography, Dialog, DialogTitle, DialogContent, IconButton } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import Calendar from './Calendar';

const Component2 = () => {
  const [openDialog, setOpenDialog] = useState(false);

  const handleDialogOpen = () => {
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 3, p: 1 }}>
      {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
        <Box
          key={day}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 1,
          }}
        >
          <Box
            sx={{
              width: 45,
              height: 45,
              borderRadius: '50%',
              bgcolor: '#f5f5f5',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Typography>{day}</Typography>
          </Box>
          
          <Box
            sx={{
              width: 35,
              height: 35,
              borderRadius: '50%',
              bgcolor: '#e0e0e0',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            {day === 'Sun' ? (
              <IconButton 
                onClick={handleDialogOpen}
                sx={{ p: 0, width: '100%', height: '100%' }}
              >
                <CalendarMonthIcon />
              </IconButton>
            ) : (
              <Typography variant="body2">0</Typography>
            )}
          </Box>
        </Box>
      ))}

      <Dialog open={openDialog} onClose={handleDialogClose} maxWidth="md" fullWidth>
        <DialogTitle>Chọn Ngày</DialogTitle>
        <DialogContent>
          <Calendar />
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default Component2;
