import React, { useState, useRef, useEffect } from 'react';
import { 
  Box, 
  TextField, 
  IconButton, 
  Typography,
  Paper,
  Avatar
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatTab = ({ groupId }) => {
  const [messages, setMessages] = useState([
    // Dummy messages for testing
    { id: 1, text: "Hello everyone!", sender: "John Doe", timestamp: "10:00 AM", isSelf: false },
    { id: 2, text: "Hi John! How are you?", sender: "You", timestamp: "10:01 AM", isSelf: true },
    { id: 3, text: "I'm doing great, thanks for asking!", sender: "John Doe", timestamp: "10:02 AM", isSelf: false }
  ]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const newMsg = {
        id: messages.length + 1,
        text: newMessage,
        sender: 'You',
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        isSelf: true
      };
      setMessages([...messages, newMsg]);
      setNewMessage('');
    }
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column', 
      height: 'calc(100vh - 200px)',
      bgcolor: '#f5f5f5'
    }}>
      {/* Messages Container */}
      <Box sx={{ 
        flexGrow: 1, 
        overflowY: 'auto', 
        p: 2,
        display: 'flex',
        flexDirection: 'column',
        gap: 2
      }}>
        {messages.map((message) => (
          <Box
            key={message.id}
            sx={{
              display: 'flex',
              justifyContent: message.isSelf ? 'flex-end' : 'flex-start',
              mb: 2
            }}
          >
            {!message.isSelf && (
              <Avatar 
                sx={{ 
                  width: 32, 
                  height: 32, 
                  mr: 1,
                  bgcolor: message.isSelf ? 'primary.main' : 'secondary.main'
                }}
              >
                {message.sender[0]}
              </Avatar>
            )}
            <Box sx={{ maxWidth: '70%' }}>
              {!message.isSelf && (
                <Typography 
                  variant="caption" 
                  sx={{ ml: 1, color: 'text.secondary' }}
                >
                  {message.sender}
                </Typography>
              )}
              <Paper
                elevation={1}
                sx={{
                  p: 1.5,
                  bgcolor: message.isSelf ? 'primary.main' : 'white',
                  color: message.isSelf ? 'white' : 'text.primary',
                  borderRadius: 2,
                  maxWidth: '100%',
                  wordWrap: 'break-word'
                }}
              >
                <Typography variant="body1">
                  {message.text}
                </Typography>
                <Typography 
                  variant="caption" 
                  sx={{ 
                    display: 'block', 
                    textAlign: 'right',
                    mt: 0.5,
                    color: message.isSelf ? 'rgba(255,255,255,0.7)' : 'text.secondary'
                  }}
                >
                  {message.timestamp}
                </Typography>
              </Paper>
            </Box>
            {message.isSelf && (
              <Avatar 
                sx={{ 
                  width: 32, 
                  height: 32, 
                  ml: 1,
                  bgcolor: 'primary.main'
                }}
              >
                {message.sender[0]}
              </Avatar>
            )}
          </Box>
        ))}
        <div ref={messagesEndRef} />
      </Box>

      {/* Message Input */}
      <Paper
        component="form"
        onSubmit={handleSendMessage}
        sx={{
          p: 2,
          boxShadow: 3,
          display: 'flex',
          alignItems: 'center',
          gap: 1
        }}
      >
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          sx={{
            '& .MuiOutlinedInput-root': {
              borderRadius: 2,
              bgcolor: 'white'
            }
          }}
        />
        <IconButton 
          type="submit"
          color="primary"
          sx={{ 
            p: '10px',
            bgcolor: 'primary.main',
            color: 'white',
            '&:hover': {
              bgcolor: 'primary.dark'
            }
          }}
        >
          <SendIcon />
        </IconButton>
      </Paper>
    </Box>
  );
};

export default ChatTab;
