import React, { useState, useEffect } from "react";
import { Box, Tabs, Tab } from "@mui/material";
import InfoTab from "./InfoTab";
import MembersTab from "./MembersTab";
import TasksTab from "./TasksTab";
import axios from 'axios';
import { Pool } from 'pg';

const GroupMenu = ({ group }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [groupInfo, setGroupInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupInfo = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get(
          `http://localhost:3000/api/groups/user-groups`,
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );

        // Lấy thông tin group hiện tại với thông tin leader và số lượng thành viên
        const query = `
          SELECT 
            g.group_id,
            g.group_name,
            g.description,
            g.creation_date,
            g.time_created,
            (
              SELECT u.user_name 
              FROM "Users" u
              JOIN "Group_Member" gm2 ON u.user_id = gm2.user_id
              WHERE gm2.group_id = g.group_id AND gm2.role = 'leader'
            ) as creator_name,
            (
              SELECT COUNT(*) 
              FROM "Group_Member" 
              WHERE group_id = g.group_id
            ) as member_count
          FROM "Group" g
          WHERE g.group_id = $1
        `;

        const result = await pool.query(query, [group.group_id]);
        
        if (result.rows.length > 0) {
          setGroupInfo({
            ...result.rows[0],
            createdBy: result.rows[0].creator_name,
            creation_date: new Date(result.rows[0].creation_date).toLocaleDateString(),
            time_created: result.rows[0].time_created.slice(0, 5)
          });
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching group info:', error);
        setLoading(false);
      }
    };

    if (group?.group_id) {
      fetchGroupInfo();
    }
  }, [group]);

  const handleChangeTab = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Box sx={{
      width: '1400px',
      height: '600px',
      bgcolor: 'background.paper',
      overflow: 'auto'
    }}>
      <Tabs 
        value={activeTab} 
        onChange={handleChangeTab} 
        indicatorColor="primary" 
        textColor="primary"
        sx={{ borderBottom: 1, borderColor: 'divider' }}
      >
        <Tab label="Info" />
        <Tab label="Members" />
        <Tab label="Tasks" />
      </Tabs>

      <Box sx={{ p: 2 }}>
        {activeTab === 0 && <InfoTab groupInfo={groupInfo} loading={loading} />}
        {activeTab === 1 && <MembersTab />}
        {activeTab === 2 && <TasksTab />}
      </Box>
    </Box>
  );
};

export default GroupMenu;
