import React, { useState } from 'react';
import { Box, IconButton, Menu, MenuItem, Dialog } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import GroupMenu from '../group/GroupMenu';
import UserProfile from '../user_info/user_menu';
import { useNavigate } from 'react-router-dom';

// các ô group

const Component1 = ({ user }) => {
  const navigate = useNavigate();
  const [groups, setGroups] = useState([1]);
  const [contextMenu, setContextMenu] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [openGroupMenu, setOpenGroupMenu] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleAddGroup = () => {
    setGroups((prev) => [...prev, prev.length + 1]);
  };

  const handleContextMenu = (event, index) => {
    event.preventDefault();
    setSelectedIndex(index); // Lưu chỉ mục của ô được chọn
    setContextMenu({
      mouseX: event.clientX + 2,
      mouseY: event.clientY - 6,
    });
  };

  const handleClose = () => {
    setContextMenu(null);
    setSelectedIndex(null);
    setIsMenuOpen(false);
  };

  const handleRemoveGroup = () => {
    setGroups((prev) => prev.filter((_, i) => i !== selectedIndex));
    handleClose();
  };

  const handleGroupClick = () => {
    setOpenGroupMenu(true);
  };

  const handleCloseGroupMenu = () => {
    setOpenGroupMenu(false);
  };

  const handleOpen = () => {
    setIsMenuOpen(true);
  };
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleOpenMainMenu = () => {
    setIsMenuOpen(true);
  };

  return (
    <Box
      sx={{
        width: '250px',
        height: '100vh',
        bgcolor: '#f5f5f5',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        border: '1px solid #ccc',
        borderRadius: 1,
        position: 'fixed',
        left: 0,
        top: 0,
        zIndex: 1000,
      }}
    >
      {/* Avatar hình tròn với ảnh nền */}
      <Box
        onClick={() => setIsMenuOpen(true)}
        sx={{
          width: 100,
          height: 100,
          borderRadius: '50%',
          backgroundImage: 'url("/todo.png")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          flexShrink: 0,
          mb: 2,
          cursor: 'pointer',
          '&:hover': {
            opacity: 0.8,
          },
        }}
      />

      {/* Nút + để thêm nhóm */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-start',
          width: '100%',
          gap: 1,
          mb: 2,
        }}
      >
        <IconButton onClick={handleAddGroup} color="primary">
          <AddIcon />
        </IconButton>
      </Box>

      {/* Danh sách các ô group */}
      <Box
        sx={{
          width: '100%',
          bgcolor: '#e0e0e0',
          p: 1,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          overflowY: 'auto',
          maxHeight: 'calc(100vh - 150px)',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#888',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb:hover': {
            backgroundColor: '#555',
          },
        }}
      >
        {groups.map((item, index) => (
          <Box
            key={item}
            onClick={handleGroupClick}
            onContextMenu={(e) => handleContextMenu(e, index)}
            sx={{
              width: '100%',
              height: 65,
              bgcolor: '#ddd',
              borderRadius: 1,
              flexShrink: 0,
              cursor: 'pointer',
              '&:hover': {
                bgcolor: '#ccc',
              },
            }}
          />
        ))}
      </Box>

      {/* Dialog hiển thị GroupMenu */}
      <Dialog
        open={openGroupMenu}
        onClose={handleCloseGroupMenu}
        maxWidth={false}
        PaperProps={{
          sx: {
            width: '90vw',
            height: '90vh',
            maxWidth: 'none',
            maxHeight: 'none',
            marginLeft: '250px',
          }
        }}
      >
        <GroupMenu />
      </Dialog>

      {/* Menu ngữ cảnh với vị trí dựa trên tọa độ chuột */}
      <Menu
        open={contextMenu !== null}
        onClose={handleClose}
        anchorReference="anchorPosition"
        anchorPosition={
          contextMenu !== null
            ? { top: contextMenu.mouseY, left: contextMenu.mouseX }
            : undefined
        }
      >
        <MenuItem onClick={handleRemoveGroup}>Xóa</MenuItem>
      </Menu>

      <UserProfile 
        open={isMenuOpen}
        onClose={handleClose}
        onLogout={handleLogout}
        openMainMenu={handleOpenMainMenu}
        user={user}
      />
    </Box>
  );
};

export default Component1;
