import React, { useState, useRef, useEffect } from 'react';
import { 
  Box, 
  TextField, 
  IconButton, 
  Typography,
  Paper,
  Avatar,
  Divider
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatTab = ({ groupId }) => {
  const [messages, setMessages] = useState([
    // Dummy messages for testing
    { id: 1, text: "Hello everyone!", sender: "John Doe", timestamp: "10:00 AM", isSelf: false },
    { id: 2, text: "Hi John! How are you?", sender: "You", timestamp: "10:01 AM", isSelf: true },
    { id: 3, text: "I'm doing great, thanks for asking!", sender: "John Doe", timestamp: "10:02 AM", isSelf: false },
    // Add more messages to test scrolling
    ...Array(10).fill(null).map((_, i) => ({
      id: i + 4,
      text: `Test message ${i + 1}`,
      sender: i % 2 === 0 ? "John Doe" : "You",
      timestamp: "10:05 AM",
      isSelf: i % 2 !== 0
    }))
  ]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);
  const chatContainerRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const newMsg = {
        id: messages.length + 1,
        text: newMessage,
        sender: 'You',
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        isSelf: true
      };
      setMessages([...messages, newMsg]);
      setNewMessage('');
    }
  };

  return (
    <Box 
      sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        height: '100%',
        position: 'relative',
        bgcolor: '#f5f5f5',
      }}
    >
      {/* Chat History Container */}
      <Box
        ref={chatContainerRef}
        sx={{
          flexGrow: 1,
          overflowY: 'auto',
          p: 2,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          mb: '80px', // Space for input box
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-track': {
            background: '#f1f1f1',
          },
          '&::-webkit-scrollbar-thumb': {
            background: '#888',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb:hover': {
            background: '#555',
          },
        }}
      >
        {messages.map((message) => (
          <Box
            key={message.id}
            sx={{
              display: 'flex',
              justifyContent: message.isSelf ? 'flex-end' : 'flex-start',
              mb: 1,
            }}
          >
            {!message.isSelf && (
              <Avatar 
                sx={{ 
                  width: 32, 
                  height: 32, 
                  mr: 1,
                  bgcolor: message.isSelf ? 'primary.main' : 'secondary.main'
                }}
              >
                {message.sender[0]}
              </Avatar>
            )}
            <Box sx={{ maxWidth: '70%' }}>
              {!message.isSelf && (
                <Typography 
                  variant="caption" 
                  sx={{ ml: 1, color: 'text.secondary' }}
                >
                  {message.sender}
                </Typography>
              )}
              <Paper
                elevation={1}
                sx={{
                  p: 1.5,
                  bgcolor: message.isSelf ? 'primary.main' : 'white',
                  color: message.isSelf ? 'white' : 'text.primary',
                  borderRadius: 2,
                  maxWidth: '100%',
                  wordWrap: 'break-word'
                }}
              >
                <Typography variant="body1">
                  {message.text}
                </Typography>
                <Typography 
                  variant="caption" 
                  sx={{ 
                    display: 'block', 
                    textAlign: 'right',
                    mt: 0.5,
                    color: message.isSelf ? 'rgba(255,255,255,0.7)' : 'text.secondary'
                  }}
                >
                  {message.timestamp}
                </Typography>
              </Paper>
            </Box>
            {message.isSelf && (
              <Avatar 
                sx={{ 
                  width: 32, 
                  height: 32, 
                  ml: 1,
                  bgcolor: 'primary.main'
                }}
              >
                {message.sender[0]}
              </Avatar>
            )}
          </Box>
        ))}
        <div ref={messagesEndRef} />
      </Box>

      {/* Fixed Input Box at Bottom */}
      <Paper
        component="form"
        onSubmit={handleSendMessage}
        sx={{
          position: 'fixed',
          bottom: 0,
          left: 0,
          right: 0,
          p: 2,
          bgcolor: 'white',
          boxShadow: 3,
          borderTop: '1px solid rgba(0, 0, 0, 0.12)',
          display: 'flex',
          alignItems: 'center',
          gap: 1,
          zIndex: 1000,
        }}
      >
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          sx={{
            '& .MuiOutlinedInput-root': {
              borderRadius: 2,
              bgcolor: 'white'
            }
          }}
        />
        <IconButton 
          type="submit"
          color="primary"
          sx={{ 
            p: '10px',
            bgcolor: 'primary.main',
            color: 'white',
            '&:hover': {
              bgcolor: 'primary.dark'
            }
          }}
        >
          <SendIcon />
        </IconButton>
      </Paper>
    </Box>
  );
};

export default ChatTab;
