import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Avatar, 
  IconButton, 
  TextField,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography
} from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';
import Component1 from './Component1';
import Component2 from './Component2';
import Component3 from './Component3';
import UserProfile from '../user_info/user_menu';

const MenuLayout = ({ onLogout }) => {
  const navigate = useNavigate();
  const [openUserMenu, setOpenUserMenu] = useState(false);
  const [user, setUser] = useState(null);
  
  // States cho tìm kiếm
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    const userStr = localStorage.getItem('user');
    if (userStr) {
      setUser(JSON.parse(userStr));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');
    navigate('/login');
  };

  // Xử lý tìm kiếm
  const handleSearch = async (value) => {
    if (!value.trim()) {
      setSearchResults([]);
      return;
    }

    try {
      setLoading(true);
      const response = await api.searchTasks(value);
      setSearchResults(response || []);
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch(searchTerm);
    }
  };

  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedTask(null);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        height: '100vh',
        width: '100vw',
        p: 1,
        borderRadius: '17px',
        overflow: 'hidden',
        backgroundColor: '#f5f5f5',
      }}
    >
      <Component1 user={user} />

      <Box sx={{ 
        flex: 1,
        display: 'flex', 
        flexDirection: 'column',
        marginLeft: '140px',
        paddingRight: '200px',
        overflow: 'auto',
        maxWidth: 'calc(100% - 200px)',
      }}>
        <Box
          sx={{
            width: '100%',
            maxWidth: '1000px',
            margin: '0 auto 40px',
            height: 80,
            backgroundImage: 'url(/logo.png)',
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            borderRadius: '16px',
            overflow: 'hidden',
          }}
        />

        {/* Thanh tìm kiếm */}
        <Box sx={{ 
          width: '100%',
          maxWidth: '1000px',
          margin: '0 auto 20px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}>
          <TextField
            variant="outlined"
            placeholder="Tìm kiếm công việc..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={handleKeyPress}
            sx={{
              width: '65%',
              '& .MuiOutlinedInput-root': {
                borderRadius: '30px',
                backgroundColor: '#fff'
              },
            }}
            InputProps={{
              endAdornment: loading && <CircularProgress size={20} />,
            }}
          />

          {/* Kết quả tìm kiếm */}
          {searchResults.length > 0 && (
            <Box sx={{ 
              width: '65%', 
              mt: 2, 
              maxHeight: '300px', 
              overflowY: 'auto',
              bgcolor: 'background.paper',
              borderRadius: 1,
              boxShadow: 3,
              p: 2,
              zIndex: 1000
            }}>
              {searchResults.map((task) => (
                <Box 
                  key={task.id}
                  onClick={() => handleTaskClick(task)}
                  sx={{
                    p: 1,
                    mb: 1,
                    borderRadius: 1,
                    '&:hover': {
                      bgcolor: 'action.hover',
                    },
                    cursor: 'pointer'
                  }}
                >
                  <Box sx={{ fontWeight: 'bold' }}>{task.task}</Box>
                  <Box sx={{ color: 'text.secondary', fontSize: '0.875rem' }}>
                    {task.taskType} - {task.timeStart}
                  </Box>
                </Box>
              ))}
            </Box>
          )}
        </Box>

        <Box sx={{
          width: '100%',
          maxWidth: '1000px',
          margin: '0 auto',
        }}>
          <Component2 />
          <Component3 />
        </Box>
      </Box>

      {/* User controls */}
      <Box
        sx={{
          position: 'absolute',
          top: 30,
          right: 30,
          display: 'flex',
          alignItems: 'center',
          gap: 1.75,
          zIndex: 20,
        }}
      >
        <IconButton
          aria-label="notifications"
          sx={{
            width: 60,
            height: 60,
            bgcolor: 'transparent',
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.1)',
            },
          }}
        >
          <NotificationsIcon sx={{ fontSize: 30 }} />
        </IconButton>

        <IconButton
          onClick={() => setOpenUserMenu(true)}
          sx={{
            p: 0,
            '&:hover': {
              opacity: 0.8,
            },
          }}
        >
          <Avatar
            alt="User Avatar"
            src="/path/to/avatar.jpg"
            sx={{ width: 57, height: 57 }}
          />
        </IconButton>
      </Box>

      {/* Dialog chi tiết task */}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Chi tiết công việc</DialogTitle>
        <DialogContent>
          {selectedTask && (
            <Box sx={{ minWidth: 400 }}>
              <Typography variant="h6" gutterBottom>
                {selectedTask.task}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Loại công việc:</strong> {selectedTask.taskType}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Trạng thái:</strong> {selectedTask.status}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Mô tả:</strong> {selectedTask.description}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Thời gian bắt đầu:</strong> {selectedTask.timeStart}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Thời gian kết thúc:</strong> {selectedTask.timeEnd}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Người tạo:</strong> {selectedTask.createdBy}
              </Typography>
              
              <Typography variant="body1" gutterBottom>
                <strong>Ngày tạo:</strong> {selectedTask.createdAt}
              </Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Đóng
          </Button>
        </DialogActions>
      </Dialog>

      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
        onLogout={handleLogout}
        user={user}
      />
    </Box>
  );
};

export default MenuLayout;
