import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000/api'
});

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('authToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

api.getAllTasks = async (year, month) => {
  try {
    // Lấy user tasks
    const userTasksResponse = await api.get(`/user-tasks/calendar/${year}/${month}`);
    
    // Lấy group tasks
    const groupTasksResponse = await api.get(`/group-tasks/calendar/${year}/${month}`);
    
    return {
      userTasks: userTasksResponse.data.tasks,
      groupTasks: groupTasksResponse.data.tasks
    };
  } catch (error) {
    throw error;
  }
};

export default api;
