import React, { useState, useEffect } from "react";
import { Box, Tabs, Tab, CircularProgress } from "@mui/material";
import InfoTab from "./InfoTab";
import MembersTab from "./MembersTab";
import TasksTab from "./TasksTab";
import axios from 'axios';

const GroupMenu = ({ group }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [groupInfo, setGroupInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupInfo = async () => {
      if (!group?.group_id) {
        setLoading(false);
        return;
      }

      try {
        console.log('Fetching group info for:', group.group_id); // Debug log
        const token = localStorage.getItem('authToken');
        const response = await axios.get(
          `http://localhost:3000/api/groups/${group.group_id}/details`,
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        console.log('Group info response:', response.data); // Debug log
        setGroupInfo(response.data.groupInfo);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching group info:', error);
        setLoading(false);
      }
    };

    fetchGroupInfo();
  }, [group]);

  const handleChangeTab = (event, newValue) => {
    setActiveTab(newValue);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{
      width: '1400px',
      height: '600px',
      bgcolor: 'background.paper',
      overflow: 'auto'
    }}>
      <Tabs 
        value={activeTab} 
        onChange={handleChangeTab} 
        indicatorColor="primary" 
        textColor="primary"
        sx={{ borderBottom: 1, borderColor: 'divider' }}
      >
        <Tab label="Info" />
        <Tab label="Members" />
        <Tab label="Tasks" />
      </Tabs>

      <Box sx={{ p: 2 }}>
        {activeTab === 0 && <InfoTab groupInfo={groupInfo} loading={loading} />}
        {activeTab === 1 && <MembersTab />}
        {activeTab === 2 && <TasksTab />}
      </Box>
    </Box>
  );
};

export default GroupMenu;
