import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { 
  Container, Box, TextField, Button, Typography, 
  Paper, Avatar 
} from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import UserProfile from './user_info/user_menu';

function AuthForm({ isLogin, setIsLogin }) {
  const [loginForm, setLoginForm] = useState({
    email: '',
    password: ''
  });

  const handleLoginChange = (e) => {
    setLoginForm({
      ...loginForm,
      [e.target.name]: e.target.value
    });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Thêm logic kiểm tra email và password ở đây
    if (loginForm.email === 'admin@example.com' && loginForm.password === 'password') {
      setIsLogin(); // Gọi callback để cập nhật trạng thái đăng nhập
    } else {
      alert('Email hoặc mật khẩu không đúng!');
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper elevation={3} sx={{ padding: 4, borderRadius: 2, width: '100%' }}>
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Avatar sx={{ m: 1, bgcolor: 'primary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Đăng Nhập
            </Typography>
          </Box>
          <Box component="form" onSubmit={handleLogin} sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email"
              name="email"
              autoComplete="email"
              autoFocus
              value={loginForm.email}
              onChange={handleLoginChange}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Mật khẩu"
              type="password"
              id="password"
              autoComplete="current-password"
              value={loginForm.password}
              onChange={handleLoginChange}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Đăng Nhập
            </Button>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
}

function MenuLayout({ onLogout }) {
  const [openUserMenu, setOpenUserMenu] = useState(false);

  return (
    <Container>
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', p: 2 }}>
        <Button onClick={() => setOpenUserMenu(true)}>
          Thông tin người dùng
        </Button>
      </Box>

      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
        onLogout={onLogout}
      />

      <Box sx={{ textAlign: 'center', mt: 4 }}>
        <Typography variant="h4">
          Chào mừng đến với To Do List
        </Typography>
      </Box>
    </Container>
  );
}

function App() {
  const [isLogin, setIsLogin] = useState(false);

  useEffect(() => {
    const loginStatus = localStorage.getItem('isLogin');
    if (loginStatus === 'true') {
      setIsLogin(true);
    }
  }, []);

  const handleLoginSuccess = () => {
    setIsLogin(true);
    localStorage.setItem('isLogin', 'true');
  };

  const handleLogout = () => {
    setIsLogin(false);
    localStorage.removeItem('isLogin');
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <Routes>
        <Route 
          path="/" 
          element={!isLogin ? 
            <AuthForm isLogin={isLogin} setIsLogin={handleLoginSuccess} /> : 
            <Navigate to="/menu" />
          } 
        />
        <Route 
          path="/menu" 
          element={isLogin ? <MenuLayout onLogout={handleLogout} /> : <Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
