import React, { useState } from "react";
import {
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TableContainer,
  Paper,
  Checkbox,
  Button,
  TablePagination,
  Dialog,
  DialogTitle,
  DialogContent,
  Typography,
} from "@mui/material";

const TaskList = () => {
  const [tasks, setTasks] = useState([
    { id: 1, task: "Project 1 (Chung) - HK 2024.1", status: "RUNNING", createdBy: "admin", createdAt: "2024-09-22 07:33:10", description: "This is the first project." },
    { id: 2, task: "C_BASIC_STACK", status: "COMPLETED", createdBy: "tung.nguyenviet", createdAt: "2023-10-26 05:56:49", description: "Basic stack implementation." },
    // Thêm các công việc khác ở đây
  ]);

  const [selectedTasks, setSelectedTasks] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(7);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  const handleToggleSelect = (id) => {
    setSelectedTasks((prevSelected) =>
      prevSelected.includes(id)
        ? prevSelected.filter((taskId) => taskId !== id)
        : [...prevSelected, id]
    );
  };

  const handleDelete = () => {
    setTasks((prevTasks) => prevTasks.filter((task) => !selectedTasks.includes(task.id)));
    setSelectedTasks([]);
  };

  const handleAdd = () => {
    const newTask = {
      id: tasks.length + 1,
      task: `New Task ${tasks.length + 1}`,
      status: "PENDING",
      createdBy: "admin",
      createdAt: new Date().toISOString().slice(0, 19).replace("T", " "),
      description: "Description for new task",
    };
    setTasks((prevTasks) => [...prevTasks, newTask]);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const displayedTasks = tasks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  const handleOpenDialog = (task) => {
    setSelectedTask(task);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedTask(null);
  };

  return (
    <TableContainer component={Paper} style={{ marginTop: "20px", maxWidth: "90%", margin: "auto" }}>
      {/* Tiêu đề */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", margin: "20px 0" }}>
        <h2 style={{ textAlign: "center", flex: 1 }}>Danh Sách Công Việc</h2>
        <div>
          <Button
            variant="contained"
            color="primary"
            style={{ marginRight: "10px" }}
            onClick={handleAdd}
          >
            +
          </Button>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleDelete}
            disabled={selectedTasks.length === 0}
          >
            -
          </Button>
        </div>
      </div>

      {/* Bảng danh sách công việc */}
      <Table>
        <TableHead>
          <TableRow>
            <TableCell />
            <TableCell style={{ fontWeight: "bold" }}>Task</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Status</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Created By</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Created At</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {displayedTasks.map((task) => (
            <TableRow key={task.id}>
              <TableCell>
                <Checkbox
                  checked={selectedTasks.includes(task.id)}
                  onChange={() => handleToggleSelect(task.id)}
                />
              </TableCell>
              <TableCell>
                <a
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    handleOpenDialog(task);
                  }}
                >
                  {task.task}
                </a>
              </TableCell>
              <TableCell>{task.status}</TableCell>
              <TableCell>{task.createdBy}</TableCell>
              <TableCell>{task.createdAt}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      {/* Phân trang */}
      <TablePagination
        rowsPerPageOptions={[7, 10, 20]}
        component="div"
        count={tasks.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />

      {/* Dialog hiển thị thông tin */}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Task Details</DialogTitle>
        <DialogContent>
          {selectedTask ? (
            <>
              <Typography variant="h6">{selectedTask.task}</Typography>
              <Typography>Status: {selectedTask.status}</Typography>
              <Typography>Created By: {selectedTask.createdBy}</Typography>
              <Typography>Created At: {selectedTask.createdAt}</Typography>
              <Typography>Description: {selectedTask.description}</Typography>
            </>
          ) : (
            <Typography>No Task Selected</Typography>
          )}
          <Button onClick={handleCloseDialog} style={{ marginTop: "20px" }} variant="contained" color="primary">
            Close
          </Button>
        </DialogContent>
      </Dialog>
    </TableContainer>
  );
};

export default TaskList;
