import React from 'react';
import { Box, Grid } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Component3 = () => {
  const items = [
    { name: 'Học Tập', category: 'study' },
    { name: 'Công Việc', category: 'work' },
    { name: 'Gia Đình', category: 'family' },
    { name: 'Hàng Ngày', category: 'daily' },
    { name: 'Hàng Tháng', category: 'monthly' },
    { name: 'Hàng Năm', category: 'yearly' },
  ];

  const navigate = useNavigate();

  const handleClick = (category) => {
    navigate(`/menu/TaskList`); // Điều hướng tới TaskList với tham số
  };

  return (
    <Box sx={{ flex: 1, pl: '10px' }}>
      {/* Logo */}
      <Box
        sx={{
          width: '70%',
          margin: '0 auto 16px',
          height: 80,
          backgroundImage: 'url(/path/to/logo.png)', // Đường dẫn tới logo
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
        }}
      />

      {/* Grid các ô */}
      <Grid
        container
        spacing={1.75}
        sx={{
          flex: 1,
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={4} sm={4} key={index}>
            <Box
              onClick={() => handleClick(item.category)} // Điều hướng với tham số
              sx={{
                width: '100%',
                height: 150,
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 24,
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
              }}
            >
              {item.name}
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Component3;
