import React, { useEffect, useState } from 'react';
import { Box, List, ListItem, ListItemText, Typography, Divider, TextField, Button } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const ChatTab = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Fetch messages from the server
    fetchMessages();
  }, []);

  const fetchMessages = async () => {
    // Implement your fetchMessages logic here
  };

  const handleSendMessage = async () => {
    // Implement your handleSendMessage logic here
  };

  return (
    <Box sx={{ 
      display: 'flex',
      flexDirection: 'column',
      height: '100%',
      backgroundColor: '#fff',
      border: '1px solid #e0e0e0',
      borderRadius: '10px',
      overflow: 'hidden'
    }}>
      {/* Khu vực hiển thị tin nhắn */}
      <Box sx={{ 
        flex: 1,
        overflowY: 'auto',
        p: 2,
        backgroundColor: '#fff',
        '&::-webkit-scrollbar': {
          width: '8px'
        },
        '&::-webkit-scrollbar-track': {
          backgroundColor: '#f5f5f5'
        },
        '&::-webkit-scrollbar-thumb': {
          backgroundColor: '#888',
          borderRadius: '4px'
        }
      }}>
        <List>
          {messages.length === 0 ? (
            <Typography align="center" color="textSecondary">
              Chưa có tin nhắn nào
            </Typography>
          ) : (
            messages.map((msg) => (
              <React.Fragment key={msg.chat_id}>
                <ListItem alignItems="flex-start">
                  <ListItemText
                    primary={
                      <Typography
                        component="span"
                        variant="body1"
                        color="text.primary"
                        sx={{ fontWeight: 'bold' }}
                      >
                        {msg.user_name}
                      </Typography>
                    }
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          color="text.primary"
                        >
                          {msg.note}
                        </Typography>
                        <Typography
                          component="span"
                          variant="caption"
                          color="text.secondary"
                          sx={{ display: 'block', mt: 0.5 }}
                        >
                          {formatTime(msg.chat_date, msg.chat_time)}
                        </Typography>
                      </React.Fragment>
                    }
                  />
                </ListItem>
                <Divider component="li" />
              </React.Fragment>
            ))
          )}
        </List>
      </Box>

      {/* Khu vực nhập tin nhắn */}
      <Box sx={{ 
        p: 2,
        backgroundColor: '#1976d2', // Màu xanh đậm
        display: 'flex',
        gap: 1,
        alignItems: 'center'
      }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Nhập tin nhắn..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
          size="small"
          sx={{
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              borderRadius: '4px',
              '& fieldset': {
                borderColor: 'transparent'
              },
              '&:hover fieldset': {
                borderColor: 'transparent'
              },
              '&.Mui-focused fieldset': {
                borderColor: 'transparent'
              }
            }
          }}
        />
        <Button
          variant="contained"
          endIcon={<SendIcon />}
          onClick={handleSendMessage}
          disabled={!newMessage.trim()}
          sx={{
            borderRadius: '4px',
            minWidth: '100px',
            backgroundColor: '#fff',
            color: '#1976d2',
            '&:hover': {
              backgroundColor: '#f5f5f5'
            }
          }}
        >
          Gửi
        </Button>
      </Box>
    </Box>
  );
};

export default ChatTab;
