import React, { useState } from 'react';
import { Box, Grid, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import TaskList from './TaskList';

const Component3 = () => {
  const items = ['Học Tập', 'Công Việc', 'Gia Đình', 'Hàng Ngày', 'Hàng Tháng', 'Hàng Năm'];
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ 
      flex: 1,
      width: '100%',
      px: 4, // padding horizontal
      py: 2  // padding vertical
    }}>
      <Grid
        container
        spacing={4}
        sx={{
          display: 'flex',
          alignItems: 'stretch', // Để các items có chiều cao bằng nhau
          justifyContent: 'center',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Box
              onClick={handleClickOpen}
              sx={{
                height: '280px', // Tăng chiều cao
                bgcolor: '#f5f5f5', // Màu nền nhẹ nhàng hơn
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '32px', // Tăng kích thước chữ
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  bgcolor: '#e3f2fd',
                  boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
                }
              }}
            >
              {item}
            </Box>
          </Grid>
        ))}
      </Grid>

      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth={false}
        PaperProps={{
          sx: {
            width: '90vw', // 90% chiều rộng màn hình
            maxHeight: '90vh', // 90% chiều cao màn hình
            borderRadius: '16px',
          }
        }}
      >
        <DialogTitle sx={{ 
          fontSize: '24px',
          fontWeight: 'bold',
          borderBottom: '1px solid #e0e0e0',
          padding: '20px 24px'
        }}>
          Danh Sách Nhiệm Vụ
        </DialogTitle>
        <DialogContent sx={{
          padding: '24px',
          minHeight: '600px',
        }}>
          <TaskList />
        </DialogContent>
        <DialogActions sx={{ padding: '16px 24px' }}>
          <Button 
            onClick={handleClose}
            variant="contained"
            sx={{
              borderRadius: '8px',
              padding: '8px 24px'
            }}
          >
            Đóng
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Component3;
