import React, { useState } from "react";
import { TextField, Button, Grid, Typography, Box } from "@mui/material";

const GroupSubmit = ({ onSubmit, onClose, groupId, editData }) => {
  const [formData, setFormData] = useState({
    taskName: editData?.group_task_name || "",
    description: editData?.task_description || "",
    timeBegin: editData?.time_begin?.substring(0, 5) || "",
    timeEnd: editData?.time_end?.substring(0, 5) || "",
    dateBegin: editData?.date_begin?.substring(0, 10) || "",
    dateEnd: editData?.date_end?.substring(0, 10) || "",
    memberId: editData?.user_id || "",
  });

  const [error, setError] = useState("");

  const validateDateTime = () => {
    const startDate = new Date(formData.dateBegin);
    const endDate = new Date(formData.dateEnd);
    
    if (startDate > endDate) {
      setError("Thời gian không hợp lệ");
      return false;
    }
    
    if (startDate.getTime() === endDate.getTime()) {
      // Nếu cùng ngày, kiểm tra giờ
      const [startHour, startMinute] = formData.timeBegin.split(':').map(Number);
      const [endHour, endMinute] = formData.timeEnd.split(':').map(Number);
      
      if (startHour > endHour || (startHour === endHour && startMinute >= endMinute)) {
        setError("Thời gian không hợp lệ");
        return false;
      }
    }
    
    setError("");
    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateDateTime()) {
      return;
    }
    if (onSubmit) {
      onSubmit(formData);
    }
    setFormData({
      taskName: "",
      description: "",
      timeBegin: "",
      timeEnd: "",
      dateBegin: "",
      dateEnd: "",
      memberId: "",
    });
  };

  return (
    <Box
      sx={{
        maxWidth: 600,
        margin: "0 auto",
        padding: 3,
        backgroundColor: "#fff",
        borderRadius: 2,
        boxShadow: 3,
      }}
    >
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          textAlign: "center",
          color: "#4682B4",
        }}
      >
        {editData ? 'Edit Task' : 'Create Task'}
      </Typography>
      {error && (
        <Typography color="error" sx={{ textAlign: "center", mb: 2 }}>
          {error}
        </Typography>
      )}
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Task Name"
              name="taskName"
              value={formData.taskName}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              multiline
              rows={4}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              label="Time Begin"
              name="timeBegin"
              type="time"
              value={formData.timeBegin}
              onChange={handleChange}
              required
              InputLabelProps={{
                shrink: true,
              }}
              error={!!error}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              label="Time End"
              name="timeEnd"
              type="time"
              value={formData.timeEnd}
              onChange={handleChange}
              required
              InputLabelProps={{
                shrink: true,
              }}
              error={!!error}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              label="Date Begin"
              name="dateBegin"
              type="date"
              value={formData.dateBegin}
              onChange={handleChange}
              required
              InputLabelProps={{
                shrink: true,
              }}
              error={!!error}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              fullWidth
              label="Date End"
              name="dateEnd"
              type="date"
              value={formData.dateEnd}
              onChange={handleChange}
              required
              InputLabelProps={{
                shrink: true,
              }}
              error={!!error}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Member ID"
              name="memberId"
              value={formData.memberId}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              fullWidth
              type="submit"
              variant="contained"
              color="primary"
              sx={{ padding: "10px 0" }}
              disabled={!!error}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default GroupSubmit;
