import React, { useState } from "react";
import SubmitTask from "./SubmitTask"; // Import giao diện SubmitTask
import {
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TableContainer,
  Paper,
  Checkbox,
  Button,
  TablePagination,
} from "@mui/material";

const TaskList = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      task: "Project 1 (Chung) - HK 2024.1",
      description: "Description of Project 1",
      status: "RUNNING",
      createdBy: "admin",
      createdAt: "2024-09-22 07:33:10",
      timeBegin: "08:00",
      timeEnd: "10:00",
    },
    // ... (Các công việc khác)
  ]);

  const [selectedTask, setSelectedTask] = useState(null); // Công việc đang chỉnh sửa
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(7);

  const handleEdit = (task) => {
    setSelectedTask(task); // Lưu công việc được chọn để chỉnh sửa
  };

  const handleCancelEdit = () => {
    setSelectedTask(null); // Quay lại danh sách công việc
  };

  if (selectedTask) {
    // Hiển thị giao diện SubmitTask khi chọn Edit
    return (
      <SubmitTask
        task={selectedTask}
        onSubmit={(updatedTask) => {
          // Cập nhật công việc trong danh sách
          setTasks((prevTasks) =>
            prevTasks.map((t) => (t.id === updatedTask.id ? updatedTask : t))
          );
          setSelectedTask(null); // Quay lại danh sách sau khi lưu
        }}
        onCancel={handleCancelEdit} // Quay lại danh sách khi nhấn Cancel
      />
    );
  }

  // Hiển thị danh sách công việc
  const displayedTasks = tasks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  return (
    <TableContainer component={Paper} style={{ marginTop: "20px", maxWidth: "90%", margin: "auto" }}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Edit</TableCell>
            <TableCell>Task</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Created By</TableCell>
            <TableCell>Created At</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {displayedTasks.map((task) => (
            <TableRow key={task.id}>
              <TableCell>
                <Button variant="contained" color="primary" onClick={() => handleEdit(task)}>
                  Edit
                </Button>
              </TableCell>
              <TableCell>{task.task}</TableCell>
              <TableCell>{task.status}</TableCell>
              <TableCell>{task.createdBy}</TableCell>
              <TableCell>{task.createdAt}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[7, 10, 20]}
        component="div"
        count={tasks.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={(event, newPage) => setPage(newPage)}
        onRowsPerPageChange={(event) => setRowsPerPage(parseInt(event.target.value, 10))}
      />
    </TableContainer>
  );
};

export default TaskList;
