import React from 'react';
import { Box, Avatar } from '@mui/material';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Component1 from './menu/Component1';
import Component2 from './menu/Component2';
import Component3 from './menu/Component3';
import Component4 from './menu/Component4';
import Calender from './menu/Calender'; // Đường dẫn đúng

const MenuLayout = () => {
  return (
    <Router>
      <Box
        sx={{
          display: 'flex',
          height: '100vh',
          width: '100vw',
          p: 1.5,
          borderRadius: '16px',
          overflow: 'hidden',
        }}
      >
        {/* Component 1 */}
        <Component1 />

        {/* Main Content */}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
          {/* Logo phía trên */}
          <Box
            sx={{
              width: '80%',
              margin: '0 auto 40px',
              height: 80,
              backgroundImage: 'url(/logo.png)',
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
              backgroundPosition: 'center',
              borderRadius: '16px',
              overflow: 'hidden',
            }}
          />

          {/* Routes cho các thành phần */}
          <Routes>
            <Route path="/" element={<Component2 />} />
            <Route path="/calendar" element={<Calender />} />
          </Routes>

          {/* Component 4 */}
          <Component4 />

          {/* Component 3 */}
          <Component3 />
        </Box>

        {/* Avatar */}
        <Box
          sx={{
            position: 'absolute',
            top: 20,
            right: 30,
            zIndex: 20,
          }}
        >
          <Avatar
            alt="User Avatar"
            src="/path/to/avatar.jpg"
            sx={{ width: 70, height: 70 }}
          />
        </Box>
      </Box>
    </Router>
  );
};

export default MenuLayout;
