import React, { useEffect, useState } from "react";
import { Box, Typography, Table, TableBody, TableRow, TableCell, Paper } from "@mui/material";
import axios from "axios";

const InfoTab = ({ group }) => {
  const [groupInfo, setGroupInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupInfo = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get(
          `http://localhost:3000/api/groups/${group.group_id}/info`,
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        setGroupInfo(response.data.groupInfo);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching group info:', error);
        setLoading(false);
      }
    };

    if (group?.group_id) {
      fetchGroupInfo();
    }
  }, [group]);

  if (loading) {
    return <Typography>Đang tải thông tin...</Typography>;
  }

  if (!groupInfo) {
    return <Typography>Không tìm thấy thông tin nhóm</Typography>;
  }

  return (
    <Box sx={{ width: '90%', margin: '0 auto' }}>
      <Typography variant="h5" mb={3} align="center" sx={{ color: '#1976d2', fontWeight: 'bold' }}>
        Group Information
      </Typography>
      <Paper elevation={2}>
        <Table size="small">
          <TableBody>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold', width: '30%' }}>Name</TableCell>
              <TableCell>{groupInfo.group_name}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Created by</TableCell>
              <TableCell>{groupInfo.creator_name}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Created at</TableCell>
              <TableCell>
                {new Date(groupInfo.creation_date).toLocaleDateString()} {groupInfo.time_created}
              </TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Number of members</TableCell>
              <TableCell>{groupInfo.member_count}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Description</TableCell>
              <TableCell>{groupInfo.description || 'No description'}</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </Paper>
    </Box>
  );
};

export default InfoTab;
