import React, { useState } from 'react';
import { 
    Container, 
    Grid, 
    Paper, 
    Typography, 
    IconButton,
    Box
} from '@mui/material';
import UserProfile from '../user_info/user_menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

function Component1() {
    const [openUserProfile, setOpenUserProfile] = useState(false);

    return (
        <Box sx={{ 
            height: '100vh',
            display: 'flex',
            flexDirection: 'column'
        }}>
            {/* Header cố định */}
            <Paper 
                elevation={3} 
                sx={{ 
                    position: 'sticky',
                    top: 0,
                    zIndex: 1100,
                    padding: 2,
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    backgroundColor: '#fff'
                }}
            >
                <Typography variant="h5">
                    Menu
                </Typography>
                <IconButton 
                    onClick={() => setOpenUserProfile(true)}
                    sx={{ padding: 1 }}
                >
                    <AccountCircleIcon fontSize="large" />
                </IconButton>
            </Paper>

            {/* Content container với scroll riêng */}
            <Container 
                maxWidth="lg" 
                sx={{ 
                    flexGrow: 1,
                    overflow: 'auto',
                    paddingY: 3
                }}
            >
                <Grid container spacing={3}>
                    {/* Nội dung menu của bạn */}
                    {/* ... */}
                </Grid>
            </Container>

            {/* UserProfile dialog */}
            <UserProfile
                open={openUserProfile}
                onClose={() => setOpenUserProfile(false)}
                onLogout={() => {
                    // Xử lý logout
                    localStorage.removeItem('authToken');
                    localStorage.removeItem('user');
                    window.location.href = '/';
                }}
            />
        </Box>
    );
}

export default Component1;
