import React, { useState } from 'react';
import { Box, Avatar, IconButton, Grid, Card, Typography } from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import Component1 from './Component1';
import Component2 from './Component2';
import Component3 from './Component3';
import Component4 from './Component4';
import UserProfile from '../user_info/user_menu';

const MenuLayout = () => {
  const [openUserMenu, setOpenUserMenu] = useState(false);

  return (
    <Box
      sx={{
        display: 'flex', // Sử dụng flexbox để sắp xếp các thành phần
        height: '100vh', // Chiều cao 100% của viewport
        width: '100vw', // Chiều rộng 100% của viewport
        p: 1.5, // Padding 1.5 đơn vị
        borderRadius: '16px', // Bo góc 16px
        overflow: 'hidden', // Ẩn nội dung tràn ra ngoài
      }}
    >
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ 
        flex: 1,
        display: 'flex', 
        flexDirection: 'column',
        marginLeft: '240px', // Giữ nguyên margin trái
        marginRight: '0',    // Bỏ margin phải nếu có
        position: 'relative' // Thêm position relative
      }}>
        {/* Logo */}
        <Box
          sx={{
            width: '80%',
            margin: '0 auto 40px',
            height: 80,
            backgroundImage: 'url(/logo.png)',
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            borderRadius: '16px',
            overflow: 'hidden',
          }}
        />

        {/* Grid Container */}
        <Grid 
          container 
          spacing={6} 
          sx={{ 
            width: '95%',    // Tăng width
            margin: '60px auto',
            padding: '40px',
            paddingRight: '100px', // Thêm padding để tránh đè lên avatar
          }}
        >
          <Grid item xs={12} md={4}>
            <Card 
              sx={{ 
                height: '300px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                cursor: 'pointer',
                borderRadius: '16px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
                  backgroundColor: '#f8f9fa'
                }
              }}
            >
              <Typography 
                variant="h3" 
                component="div" 
                sx={{ 
                  color: '#1976d2',
                  fontWeight: 'bold'
                }}
              >
                Học Tập
              </Typography>
            </Card>
          </Grid>
          {/* Các Grid items khác tương tự */}
        </Grid>
      </Box>

      {/* Avatar và nút chuông */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          right: 30,
          display: 'flex',
          alignItems: 'center',
          gap: 1.75,
          zIndex: 1000, // Tăng z-index để đảm bảo hiển thị trên các phần tử khác
        }}
      >
        {/* Nút hình chuông */}
        <IconButton
          aria-label="notifications"
          sx={{
            width: 60,
            height: 60,
            bgcolor: 'transparent', // Nền trong suốt
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.1)', // Màu nền khi hover
            },
          }}
        >
          <NotificationsIcon sx={{ fontSize: 30 }} />
        </IconButton>

        {/* Avatar với onClick */}
        <IconButton
          onClick={() => setOpenUserMenu(true)}
          sx={{
            p: 0, // Padding 0
            '&:hover': {
              opacity: 0.8, // Độ mờ khi hover
            },
          }}
        >
          
          <Avatar
            alt="User Avatar"
            src="/path/to/avatar.jpg"
            sx={{ 
              width: 57, 
              height: 57 
            }}
          />
        </IconButton>
      </Box>

      {/* UserProfile component */}
      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
      />
    </Box>
  );
};

export default MenuLayout;
