import React, { useState } from 'react';
import { Box, Grid, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import TaskList from './TaskList'; // Import TaskList Component

const Component3 = () => {
  const items = ['Học Tập', 'Công Việc', 'Gia Đình', 'Hàng Ngày', 'Hàng Tháng', 'Hàng Năm'];

  // State để điều khiển việc mở/đóng Dialog
  const [open, setOpen] = useState(false);

  // Hàm mở dialog
  const handleClickOpen = () => {
    setOpen(true);
  };

  // Hàm đóng dialog
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ flex: 1, pl: '10px' }}>
      {/* Logo */}
      <Box
        sx={{
          width: '70%',
          margin: '0 auto 16px',
          height: 80,
          backgroundImage: 'url(/path/to/logo.png)', // Đường dẫn tới logo
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
        }}
      />

      {/* Grid các ô */}
      <Grid
        container
        spacing={1.75}
        sx={{
          flex: 1,
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={4} sm={4} key={index}>
            <Box
              onClick={handleClickOpen} // Gọi hàm mở dialog
              sx={{
                width: '100%',
                height: 150,
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 24,
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
              }}
            >
              {item}
            </Box>
          </Grid>
        ))}
      </Grid>

      {/* Dialog */}
      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth="lg"  // Đảm bảo tối đa chiều rộng là lg
        fullWidth // Chiếm 100% chiều rộng của màn hình
      >
        <DialogTitle>Danh Sách Nhiệm Vụ</DialogTitle>
        <DialogContent
          sx={{
            width: '76vw', // Chiếm 2/3 chiều rộng màn hình
            maxHeight: '90vh', // Chiều cao chiếm tối đa 90% chiều cao màn hình
            // overflowY: 'auto', // Cho phép cuộn khi nội dung vượt quá
          }}
        >
          <TaskList /> {/* Nội dung của TaskList */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Đóng</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Component3;
