import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Typography, Container, Box, Paper, Avatar, Divider, 
  Button, Dialog, DialogContent, DialogTitle, IconButton, 
  List, ListItem, ListItemAvatar, ListItemText,
  TextField, CircularProgress
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import CloseIcon from '@mui/icons-material/Close';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import api from '../utils/api';
import axios from 'axios';

const UserProfile = ({ open, onClose, onLogout, user }) => {
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formData, setFormData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    username: user?.username || '',
    email: user?.email || ''
  });

  useEffect(() => {
    if (user) {
      setFormData(prev => ({
        ...prev,
        username: user.username || '',
        email: user.email || ''
      }));
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      if (formData.newPassword !== formData.confirmPassword) {
        throw new Error('Mật khẩu mới không khớp');
      }

      const response = await api.post('/auth/change-password', {
        currentPassword: formData.currentPassword,
        newPassword: formData.newPassword
      });

      setSuccess('Đổi mật khẩu thành công');
      setShowChangePassword(false);
      setFormData(prev => ({
        ...prev,
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      }));
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await api.put('/users/profile', {
        username: formData.username,
        email: formData.email
      });

      localStorage.setItem('user', JSON.stringify(response.data));
      setSuccess('Cập nhật thông tin thành công');
      setShowEditProfile(false);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  const renderMainMenu = () => (
    <>
      <DialogTitle>
        Thông tin tài khoản
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <List>
          <ListItem button onClick={() => setShowEditProfile(true)}>
            <ListItemAvatar>
              <Avatar>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary="Thông tin cá nhân" secondary={user?.username} />
          </ListItem>
          
          <Divider variant="inset" component="li" />
          
          <ListItem button onClick={() => setShowChangePassword(true)}>
            <ListItemAvatar>
              <Avatar>
                <LockIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary="Đổi mật khẩu" />
          </ListItem>
          
          <Divider variant="inset" component="li" />
          
          <ListItem button onClick={onLogout}>
            <ListItemAvatar>
              <Avatar>
                <ExitToAppIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary="Đăng xuất" />
          </ListItem>
        </List>
      </DialogContent>
    </>
  );

  const renderChangePassword = () => (
    <>
      <DialogTitle>
        <IconButton
          onClick={() => setShowChangePassword(false)}
          sx={{ mr: 1 }}
        >
          <ArrowBackIcon />
        </IconButton>
        Đổi mật khẩu
      </DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleChangePassword} sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            name="currentPassword"
            label="Mật khẩu hiện tại"
            type="password"
            value={formData.currentPassword}
            onChange={handleInputChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="newPassword"
            label="Mật khẩu mới"
            type="password"
            value={formData.newPassword}
            onChange={handleInputChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="confirmPassword"
            label="Xác nhận mật khẩu mới"
            type="password"
            value={formData.confirmPassword}
            onChange={handleInputChange}
          />
          {error && (
            <Typography color="error" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}
          {success && (
            <Typography color="success.main" sx={{ mt: 1 }}>
              {success}
            </Typography>
          )}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Đổi mật khẩu'}
          </Button>
        </Box>
      </DialogContent>
    </>
  );

  const renderEditProfile = () => (
    <>
      <DialogTitle>
        <IconButton
          onClick={() => setShowEditProfile(false)}
          sx={{ mr: 1 }}
        >
          <ArrowBackIcon />
        </IconButton>
        Chỉnh sửa thông tin
      </DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleUpdateProfile} sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            name="username"
            label="Tên người dùng"
            value={formData.username}
            onChange={handleInputChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="email"
            label="Email"
            type="email"
            value={formData.email}
            onChange={handleInputChange}
          />
          {error && (
            <Typography color="error" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}
          {success && (
            <Typography color="success.main" sx={{ mt: 1 }}>
              {success}
            </Typography>
          )}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Cập nhật'}
          </Button>
        </Box>
      </DialogContent>
    </>
  );

  return (
    <Dialog open={open} onClose={onClose}>
      {showChangePassword ? renderChangePassword() :
       showEditProfile ? renderEditProfile() :
       renderMainMenu()}
    </Dialog>
  );
};

export default UserProfile;
