import React, { useState } from 'react';
import { Box, IconButton } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';

const Component1 = () => {
  const [groups, setGroups] = useState([1]); // Bắt đầu với 1 group

  const handleAddGroup = () => {
    setGroups((prev) => [...prev, prev.length + 1]); // Thêm group mới
  };

  const handleRemoveGroup = (index) => {
    setGroups((prev) => prev.filter((_, i) => i !== index)); // Xóa group tại chỉ mục được chọn
  };

  return (
    <Box
      sx={{
        width: '250px',
        height: '100vh',
        bgcolor: '#f5f5f5',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        border: '1px solid #ccc',
        borderRadius: 1,
      }}
    >
      {/* Avatar hình tròn */}
      <Box
        sx={{
          width: 50,
          height: 50,
          borderRadius: '50%',
          bgcolor: '#bbb',
          mb: 2,
        }}
      ></Box>

      {/* Nút + để thêm nhóm */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-start',
          width: '100%',
          gap: 1,
          mb: 2,
        }}
      >
        <IconButton onClick={handleAddGroup} color="primary">
          <AddIcon />
        </IconButton>
      </Box>

      {/* Danh sách các ô group */}
      <Box
        sx={{
          width: '100%',
          bgcolor: '#e0e0e0',
          p: 1,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          overflowY: 'auto',
          maxHeight: 'calc(100vh - 150px)',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#888',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb:hover': {
            backgroundColor: '#555',
          },
        }}
      >
        {groups.map((item, index) => (
          <Box
            key={item}
            onContextMenu={(e) => {
              e.preventDefault(); // Ngăn menu chuột phải mặc định xuất hiện
              handleRemoveGroup(index); // Xóa nhóm khi nhấp chuột phải
            }}
            sx={{
              width: '100%',
              height: 50,
              bgcolor: '#ddd',
              borderRadius: 1,
              flexShrink: 0,
              cursor: 'pointer',
            }}
          ></Box>
        ))}
      </Box>
    </Box>
  );
};

export default Component1;
