import React, { useState, useEffect } from 'react';
import { Box, Grid, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import TaskList from './TaskList';
import axios from 'axios';

const Component3 = () => {
  const [open, setOpen] = useState(false);
  const [selectedTaskType, setSelectedTaskType] = useState(null);
  const [tasks, setTasks] = useState([]);

  const items = [
    { id: 1, name: 'Học Tập' },
    { id: 2, name: 'Công Việc' },
    { id: 3, name: 'Gia Đình' },
    { id: 4, name: 'Hàng Ngày' },
    { id: 5, name: 'Hàng Tháng' },
    { id: 6, name: 'Hàng Năm' }
  ];

  const handleClickOpen = async (taskTypeId) => {
    setSelectedTaskType(taskTypeId);
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.get(
        `http://localhost:3000/api/tasks/${taskTypeId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Có lỗi xảy ra khi lấy danh sách công việc!');
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedTaskType(null);
  };

  return (
    <Box sx={{ 
      flex: 1,
      pl: '8px',
      mt: 12,
    }}>
      <Grid container spacing={2.5} sx={{
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} lg={4} key={item.id}>
            <Box
              onClick={() => handleClickOpen(item.id)}
              sx={{
                width: '100%',
                height: 150,
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 25,
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
              }}
            >
              {item.name}
            </Box>
          </Grid>
        ))}
      </Grid>

      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth={false}
      >
        <DialogTitle>Danh Sách Nhiệm Vụ</DialogTitle>
        <DialogContent sx={{
          width: 900,
          height: 600,
          overflowY: 'auto',
        }}>
          <TaskList tasks={tasks} />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Đóng</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Component3;
