import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Typography, Container, Box, Paper, Avatar, Divider, 
  Button, Dialog, DialogContent, DialogTitle, IconButton, 
  List, ListItem, ListItemAvatar, ListItemText,
  TextField, CircularProgress
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import CloseIcon from '@mui/icons-material/Close';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import api from '../utils/api';

function UserProfile({ open, onClose, onLogout }) {
  console.log('UserProfile rendered, open:', open);
  
  const [openInfo, setOpenInfo] = useState(false);
  const [openChangePassword, setOpenChangePassword] = useState(false);
  const [openChangeName, setOpenChangeName] = useState(false);
  const [showMainMenu, setShowMainMenu] = useState(true);
  
  const [passwordForm, setPasswordForm] = useState({
    oldPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const [nameForm, setNameForm] = useState({
    oldName: '',
    newName: ''
  });

  const [userInfo, setUserInfo] = useState({
    id: '',
    email: '',
    name: '',
  });
  const [loading, setLoading] = useState(false);
  const [profileLoading, setProfileLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        setProfileLoading(true);
        const response = await api.get('http://localhost:3000/api/users/profile');
        setUserInfo(response.data.user);
        setError(null);
      } catch (error) {
        console.error('Lỗi khi lấy thông tin user:', error);
        setError('Không thể lấy thông tin người dùng');
      } finally {
        setProfileLoading(false);
      }
    };

    if (open) {
      fetchUserProfile();
    }
  }, [open]);

  const handleInfoOpen = () => {
    setOpenInfo(true);
    setShowMainMenu(false);
  };
  const handleInfoClose = () => {
    setOpenInfo(false);
    setShowMainMenu(true);
  };
  const handleChangePasswordOpen = () => {
    setOpenChangePassword(true);
    setShowMainMenu(false);
  };
  const handleChangePasswordClose = () => {
    setOpenChangePassword(false);
    setShowMainMenu(true);
  };
  const handleChangeNameOpen = () => {
    setOpenChangeName(true);
    setShowMainMenu(false);
  };
  const handleChangeNameClose = () => {
    setOpenChangeName(false);
    setShowMainMenu(true);
  };

  const handleNavigation = (action) => {
    switch(action) {
      case "Thông tin người dùng":
        handleInfoOpen();
        break;
      case "Đổi mật khẩu":
        setOpenChangePassword(true);
        break;
      case "Đổi tên":
        setOpenChangeName(true);
        break;
      case "Đăng xuất":
        if (onLogout) onLogout();
        onClose();
        break;
      default:
        break;
    }
  };

  const handlePasswordChange = (e) => {
    setPasswordForm({
      ...passwordForm,
      [e.target.name]: e.target.value
    });
  };

  const handleNameChange = (e) => {
    setNameForm({
      ...nameForm,
      [e.target.name]: e.target.value
    });
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);
      
      if (passwordForm.newPassword !== passwordForm.confirmPassword) {
        throw new Error('Mật khẩu mới không khớp');
      }

      await api.post('/auth/change-password', {
        oldPassword: passwordForm.oldPassword,
        newPassword: passwordForm.newPassword
      });

      setSuccess('Đổi mật khẩu thành công');
      setPasswordForm({
        oldPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
      handleChangePasswordClose();
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleNameSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);
      
      const response = await api.put('/users/profile', {
        name: nameForm.newName
      });

      const updatedUser = { ...userInfo, name: nameForm.newName };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUserInfo(updatedUser);
      
      setSuccess('Đổi tên thành công');
      setNameForm({ newName: '' });
      handleChangeNameClose();
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  if (profileLoading) {
    return <CircularProgress />;
  }

  return (
    <Container maxWidth="xs" sx={{ paddingY: 4 }}>
      {/* Menu Modal */}
      <Dialog
        open={open && showMainMenu}
        onClose={onClose}
        fullWidth
        maxWidth="xs"
        PaperProps={{ sx: { borderRadius: 4 } }}
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="h6">{userInfo.name}</Typography>
          <IconButton onClick={onClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent sx={{ textAlign: 'center' }}>
          <Avatar
            sx={{
              width: 80,
              height: 80,
              margin: 'auto',
              marginBottom: 2,
              backgroundColor: '#3f51b5',
            }}
          >
            <PersonIcon fontSize="large" />
          </Avatar>
          <Typography variant="subtitle1">To Do List</Typography>
          <List>
            <ListItem button onClick={() => handleNavigation("Thông tin người dùng")}>
              <ListItemAvatar>
                <Avatar><PersonIcon /></Avatar>
              </ListItemAvatar>
              <ListItemText primary="Thông tin người dùng" />
            </ListItem>
            <ListItem button onClick={() => handleNavigation("Đổi tên")}>
              <ListItemAvatar>
                <Avatar><PersonIcon /></Avatar>
              </ListItemAvatar>
              <ListItemText primary="Đổi tên" />
            </ListItem>
            <ListItem button onClick={() => handleNavigation("Đổi mật khẩu")}>
              <ListItemAvatar>
                <Avatar><LockIcon /></Avatar>
              </ListItemAvatar>
              <ListItemText primary="Đổi mật khẩu" />
            </ListItem>
            <ListItem button onClick={() => handleNavigation("Đăng xuất")}>
              <ListItemAvatar>
                <Avatar><ExitToAppIcon /></Avatar>
              </ListItemAvatar>
              <ListItemText primary="Đăng xuất" />
            </ListItem>
          </List>
        </DialogContent>
      </Dialog>

      {/* Info Modal */}
      <Dialog
        open={openInfo}
        onClose={handleInfoClose}
        fullWidth
        maxWidth="xs"
        PaperProps={{ sx: { borderRadius: 4 } }}
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <IconButton onClick={handleInfoClose}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6">Thông Tin Chi Tiết</Typography>
          <IconButton onClick={handleInfoClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Paper elevation={3} sx={{ padding: 4, borderRadius: 4, textAlign: 'center' }}>
            <Avatar
              sx={{
                width: 60,
                height: 60,
                margin: 'auto',
                marginBottom: 2,
                backgroundColor: '#3f51b5',
              }}
            >
              <PersonIcon fontSize="large" />
            </Avatar>

            <Typography variant="h6" gutterBottom>
              Thông Tin Người Dùng
            </Typography>

            <Divider sx={{ marginY: 2 }} />

            <Box sx={{ textAlign: 'left', paddingX: 2 }}>
              <Typography variant="body1" sx={{ marginBottom: 1 }}>
                <strong>ID:</strong> {userInfo.id}
              </Typography>
              <Typography variant="body1" sx={{ marginBottom: 1 }}>
                <strong>Email:</strong> {userInfo.email}
              </Typography>
              <Typography variant="body1" sx={{ marginBottom: 1 }}>
                <strong>Tên Người Dùng:</strong> {userInfo.name}
              </Typography>
              <Typography variant="body1" sx={{ marginBottom: 1 }}>
                <strong>Mật Khẩu:</strong> ********
              </Typography>
            </Box>
          </Paper>
        </DialogContent>
      </Dialog>

      {/* Change Password Modal */}
      <Dialog
        open={openChangePassword}
        onClose={handleChangePasswordClose}
        fullWidth
        maxWidth="xs"
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <IconButton onClick={handleChangePasswordClose}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6">Đổi Mật Khẩu</Typography>
          <IconButton onClick={handleChangePasswordClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handlePasswordSubmit} sx={{ mt: 2 }}>
            <TextField
              fullWidth
              margin="normal"
              label="Mật khẩu cũ"
              type="password"
              name="oldPassword"
              value={passwordForm.oldPassword}
              onChange={handlePasswordChange}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Mật khẩu mới"
              type="password"
              name="newPassword"
              value={passwordForm.newPassword}
              onChange={handlePasswordChange}
            />
            <TextField
              fullWidth
              margin="normal"
              label="Nhập lại mật khẩu mới"
              type="password"
              name="confirmPassword"
              value={passwordForm.confirmPassword}
              onChange={handlePasswordChange}
            />
            {error && (
              <Typography color="error" sx={{ mt: 1 }}>
                {error}
              </Typography>
            )}
            {success && (
              <Typography color="success.main" sx={{ mt: 1 }}>
                {success}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3 }}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Xác nhận'}
            </Button>
          </Box>
        </DialogContent>
      </Dialog>

      {/* Change Name Modal */}
      <Dialog
        open={openChangeName}
        onClose={handleChangeNameClose}
        fullWidth
        maxWidth="xs"
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <IconButton onClick={handleChangeNameClose}>
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6">Đổi Tên</Typography>
          <IconButton onClick={handleChangeNameClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handleNameSubmit} sx={{ mt: 2 }}>
            <TextField
              fullWidth
              margin="normal"
              label="Tên mới"
              name="newName"
              value={nameForm.newName}
              onChange={handleNameChange}
            />
            {error && (
              <Typography color="error" sx={{ mt: 1 }}>
                {error}
              </Typography>
            )}
            {success && (
              <Typography color="success.main" sx={{ mt: 1 }}>
                {success}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3 }}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Xác nhận'}
            </Button>
          </Box>
        </DialogContent>
      </Dialog>
    </Container>
  );
}

export default UserProfile;
