import React, { useState, useEffect } from 'react';
import { Box, Typography, Dialog, DialogTitle, DialogContent, IconButton } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import Calendar from './Calendar';
import api from '../utils/api';

const Component2 = () => {
  const [openDialog, setOpenDialog] = useState(false);
  const [currentWeekTasks, setCurrentWeekTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const tasks = [];
        for (let type = 1; type <= 6; type++) {
          const data = await api.getTasksByType(type);
          if (data) {
            tasks.push(...data);
          }
        }
        console.log('Fetched tasks:', tasks);
        setCurrentWeekTasks(tasks);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
    // Refresh mỗi 30 giây
    const intervalId = setInterval(fetchTasks, 30000);
    return () => clearInterval(intervalId);
  }, []);

  const getCurrentWeekDates = () => {
    const now = new Date();
    const currentDay = now.getDay(); // 0 = CN, 1 = T2, ...
    const diff = currentDay === 0 ? -6 : 1 - currentDay; // Điều chỉnh để lấy thứ 2
    
    const monday = new Date(now);
    monday.setDate(now.getDate() + diff);

    const weekDays = [];
    const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

    for (let i = 0; i < 7; i++) {
      const date = new Date(monday);
      date.setDate(monday.getDate() + i);
      weekDays.push({
        name: days[i],
        date: date,
        isToday: date.toDateString() === now.toDateString()
      });
    }

    return weekDays;
  };

  const checkDateHasTask = (date) => {
    return currentWeekTasks.some(task => {
      const [startDateStr] = task.timeStart.split(' ');
      const [startDay, startMonth, startYear] = startDateStr.split('/').map(Number);
      const startDate = new Date(startYear, startMonth - 1, startDay);
      
      const [endDateStr] = task.timeEnd.split(' ');
      const [endDay, endMonth, endYear] = endDateStr.split('/').map(Number);
      const endDate = new Date(endYear, endMonth - 1, endDay);

      const checkDate = new Date(date);
      checkDate.setHours(0, 0, 0, 0);
      return checkDate >= startDate && checkDate <= endDate;
    });
  };

  const handleDialogOpen = () => {
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  const weekDays = getCurrentWeekDates();

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 3, p: 5 }}>
      {weekDays.map((day) => (
        <Box
          key={day.name}
          sx={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            gap: 3,
          }}
        >
          <Box
            sx={{
              width: 45,
              height: 45,
              borderRadius: '50%',
              bgcolor: checkDateHasTask(day.date) ? '#1976d2' : 
                      day.isToday ? '#f57c00' : '#e0e0e0',
              color: (checkDateHasTask(day.date) || day.isToday) ? '#fff' : '#000',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              position: 'relative',
              transition: 'all 0.3s ease',
              '&:hover': {
                transform: 'scale(1.1)',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
              }
            }}
          >
            <Typography>{day.name}</Typography>
          </Box>

          {day.name === 'Sun' && (
            <Box
              sx={{
                width: 38,
                height: 38,
                borderRadius: '39%',
                bgcolor: '#1976d2',
                color: '#fff',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <IconButton 
                onClick={handleDialogOpen} 
                sx={{ color: '#fff' }}
              >
                <CalendarMonthIcon />
              </IconButton>
            </Box>
          )}
        </Box>
      ))}

      <Dialog 
        open={openDialog} 
        onClose={handleDialogClose}
        PaperProps={{
          sx: {
            width: '700px',
            height: '700px',
            maxWidth: '90vw',
            maxHeight: '90vh'
          }
        }}
      >
        <DialogTitle sx={{ 
          borderBottom: '1px solid #e0e0e0',
          backgroundColor: '#f5f5f5',
          padding: '16px 24px'
        }}>
          Lịch Công Việc
        </DialogTitle>
        <DialogContent sx={{ 
          padding: '25px',
          overflow: 'auto'
        }}>
          <Calendar />
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default Component2;
