import React, { useState } from 'react';
import { Box, Avatar, IconButton } from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import Component1 from './Component1';
import Component2 from './Component2';
import Component3 from './Component3';
import Component4 from './Component4';
import UserProfile from '../user_info/user_menu';

const MenuLayout = () => {
  const [openUserMenu, setOpenUserMenu] = useState(false);

  return (
    <Box
      sx={{
        display: 'flex', // Sử dụng flexbox để sắp xếp các thành phần
        height: '100vh', // Chiều cao 100% của viewport
        width: '115vw', // Chiều rộng 100% của viewport
        p: 0.7, // Padding 1.5 đơn vị
        borderRadius: '16px', // Bo góc 16px
        overflow: 'hidden', // Ẩn nội dung tràn ra ngoài
      }}
    >
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ 
        flex: 1, // Chiếm hết không gian còn lại
        display: 'flex', 
        flexDirection: 'column', // Sắp xếp các phần tử theo chiều dọc
        marginLeft: '240px', // Khoảng cách với Component1
        overflow: 'auto', // Cho phép cuộn dọc khi nội dung vượt quá
      }}>
        {/* Logo phía trên Lịch với borderRadius */}
        <Box
          sx={{
            width: '80%', // Chiều rộng 80% của phần tử cha
            margin: '0 auto 40px', // Căn giữa và margin bottom 40px
            height: 80, // Chiều cao cố định 80px
            backgroundImage: 'url(/logo.png)',
            backgroundSize: 'contain', // Giữ tỷ lệ ảnh và vừa với container
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            borderRadius: '16px',
            overflow: 'hidden',
          }}
        />

        {/* Component 2 - Lịch */}
        <Component2 />

        {/* Component 4 - Thanh tìm kiếm */}
        <Component4 />

        {/* Component 3 */}
        <Component3 />
      </Box>

      {/* Avatar và nút chuông */}
      <Box
        sx={{
          position: 'absolute', // Định vị tuyệt đối
          top: 20, // Cách top 20px
          right: 30, // Cách right 30px
          display: 'flex',
          alignItems: 'center', // Căn giữa theo chiều dọc
          gap: 1.75, // Khoảng cách giữa các phần tử con
          zIndex: 20, // Lớp hiển thị trên các phần tử khác
        }}
      >
        {/* Nút hình chuông */}
        <IconButton
          aria-label="notifications"
          sx={{
            width: 60,
            height: 60,
            bgcolor: 'transparent', // Nền trong suốt
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.1)', // Màu nền khi hover
            },
          }}
        >
          <NotificationsIcon sx={{ fontSize: 30 }} />
        </IconButton>

        {/* Avatar với onClick */}
        <IconButton
          onClick={() => setOpenUserMenu(true)}
          sx={{
            p: 0, // Padding 0
            '&:hover': {
              opacity: 0.8, // Độ mờ khi hover
            },
          }}
        >
          <Avatar
            alt="User Avatar"
            src="/path/to/avatar.jpg"
            sx={{ 
              width: 57, 
              height: 57 
            }}
          />
        </IconButton>
      </Box>

      {/* UserProfile component */}
      <UserProfile 
        open={openUserMenu}
        onClose={() => setOpenUserMenu(false)}
      />
    </Box>
  );
};

export default MenuLayout;
