import React from 'react';
import { Box, Grid } from '@mui/material';

const Component3 = () => {
  const items = ['Học Tập', 'Công Việc', 'Gia Đình', 'Hàng Ngày', 'Hàng Tháng', 'Hàng Năm'];

  return (
    <Box sx={{ flex: 1, pl: '10px' }}> {/* Thêm padding-left 2px */}
      {/* Logo */}
      <Box
        sx={{
          width: '70%',
          margin: '0 auto 16px',
          height: 80,
          backgroundImage: 'url(/path/to/logo.png)', // Đường dẫn tới logo
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
        }}
      />

      {/* Grid các ô */}
      <Grid
        container
        spacing={1.75} // Khoảng cách giữa các hàng và cột
        sx={{
          flex: 1,
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={4} sm={4} key={index}>
            <Box
              sx={{
                width: '100%',
                height: 150, // Chiều cao của ô
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 24, // Tăng cỡ chữ lên
                fontWeight: 'bold',
                color: '#1976d2', // Màu chữ xanh lam biển
              }}
            >
              {item} ({index + 1})
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Component3;
