import React from 'react';
import { Box, Typography } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'; // Import biểu tượng lịch
import { useNavigate, BrowserRouter } from 'react-router-dom'; // Import useNavigate và BrowserRouter

const Component2 = () => {
  const navigate = useNavigate(); // Khởi tạo hàm điều hướng

  const handleCalendarClick = () => {
    navigate('/menu/Calendar'); // Chuyển hướng đến trang Calendar
  };

  return (
    <BrowserRouter> {/* Bọc trong BrowserRouter nếu không có Router bên ngoài */}
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 3, p: 1 }}>
        {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
          <Box
            key={day}
            sx={{
              display: 'flex',
              flexDirection: 'row', // Đặt hàng ngang cho ngày và biểu tượng
              alignItems: 'center',
              gap: 2.5,
            }}
          >
            <Box
              sx={{
                width: 45,
                height: 45,
                borderRadius: '50%',
                bgcolor: '#f5f5f5',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <Typography>{day}</Typography>
            </Box>
            {day === 'Sun' && (
              <Box
                sx={{
                  width: 38,
                  height: 38,
                  borderRadius: '39%',
                  bgcolor: '#1976d2',
                  color: '#fff',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  cursor: 'pointer', // Thêm cursor để thể hiện có thể click
                }}
                onClick={handleCalendarClick} // Gắn sự kiện click
              >
                <CalendarMonthIcon />
              </Box>
            )}
          </Box>
        ))}
      </Box>
    </BrowserRouter>
  );
};

export default Component2;
