import React from 'react';
import { Box, Typography } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'; // Import biểu tượng lịch
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom'; // Import router
import Calendar from './menu/Calendar';; // Import component Calendar

// Component chính
const Component2 = () => {
  const navigate = useNavigate(); // Hàm chuyển hướng

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 3, p: 1 }}>
      {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
        <Box
          key={day}
          sx={{
            display: 'flex',
            flexDirection: 'row', // Đặt hàng ngang cho ngày và biểu tượng
            alignItems: 'center',
            gap: 2.5,
          }}
        >
          <Box
            sx={{
              width: 45,
              height: 45,
              borderRadius: '50%',
              bgcolor: '#f5f5f5',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Typography>{day}</Typography>
          </Box>
          {day === 'Sun' && (
            <Box
              onClick={() => navigate('/calendar')} // Chuyển hướng đến /calendar
              sx={{
                width: 38,
                height: 38,
                borderRadius: '39%', // bo góc
                bgcolor: '#1976d2',
                color: '#fff',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                cursor: 'pointer', // Thêm hiệu ứng trỏ chuột
              }}
            >
              <CalendarMonthIcon />
            </Box>
          )}
        </Box>
      ))}
    </Box>
  );
};

// Thành phần quản lý routes
const ComponentWithRoutes = () => {
  return (
    <Router>
      <Routes>
        {/* Đường dẫn chính */}
        <Route path="/" element={<Component2 />} />
        {/* Đường dẫn đến Calendar */}
        <Route path="/calendar" element={<Calendar />} />
      </Routes>
    </Router>
  );
};

export default ComponentWithRoutes;
