import React from 'react';
import { Box, Avatar } from '@mui/material';
import Component1 from './menu/Component1';
import Component2 from './menu/Component2';
import Component3 from './menu/Component3';
import Component4 from './menu/Component4';

const MenuLayout = () => {
  return (
    <Box sx={{ display: 'flex', height: '100vh', width: '100vw', p: 2 }}>
      {/* Component 1 */}
      <Component1 />

      {/* Main Content */}
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* Logo phía trên Lịch */}
        <Box
          sx={{
            width: '70%',
            margin: '0 auto 16px',
            height: 100,
            backgroundImage: 'url(/logo.png)', // Đường dẫn logo
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
          }}
        />

        {/* Component 2 - Lịch */}
        <Component2 />

        {/* Component 4 - Thanh tìm kiếm */}
        <Component4 />

        {/* Component 3 */}
        <Component3 />
      </Box>

      {/* Hình tròn avatar nhỏ ở góc trên bên phải */}
      <Box
        sx={{
          position: 'absolute',
          top: 20,
          right: 16,
          zIndex: 10, // Đảm bảo nó nằm trên các phần tử khác
        }}
      >
        <Avatar 
          alt="User Avatar" 
          src="/path/to/avatar.jpg"  // Đường dẫn ảnh avatar
          sx={{ width: 40, height: 40 }}  // Kích thước avatar
        />
      </Box>
    </Box>
  );
};

export default MenuLayout;
