import React, { useState } from 'react';
import { Box, TextField, CircularProgress } from '@mui/material';
import { useDebounce } from 'use-debounce';
import api from '../utils/api';

const Component4 = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  
  // Debounce search term để tránh gọi API quá nhiều
  const [debouncedSearchTerm] = useDebounce(searchTerm, 500);

  // Xử lý tìm kiếm
  const handleSearch = async (value) => {
    if (!value.trim()) {
      setSearchResults([]);
      return;
    }

    try {
      setLoading(true);
      const response = await api.get(`/tasks/search?searchTerm=${value}`);
      setSearchResults(response.data.tasks);
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Effect để xử lý debounced search
  React.useEffect(() => {
    handleSearch(debouncedSearchTerm);
  }, [debouncedSearchTerm]);

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center', 
      mt: '35px' 
    }}>
      <TextField
        variant="outlined"
        placeholder="Tìm kiếm công việc..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        sx={{
          width: '65%',
          '& .MuiOutlinedInput-root': {
            borderRadius: '30px',
          },
        }}
        InputProps={{
          endAdornment: loading && <CircularProgress size={20} />,
        }}
      />

      {/* Hiển thị kết quả tìm kiếm */}
      {searchResults.length > 0 && (
        <Box sx={{ 
          width: '65%', 
          mt: 2, 
          maxHeight: '300px', 
          overflowY: 'auto',
          bgcolor: 'background.paper',
          borderRadius: 1,
          boxShadow: 3,
          p: 2
        }}>
          {searchResults.map((task) => (
            <Box 
              key={task.id}
              sx={{
                p: 1,
                mb: 1,
                borderRadius: 1,
                '&:hover': {
                  bgcolor: 'action.hover',
                },
                cursor: 'pointer'
              }}
            >
              <Box sx={{ fontWeight: 'bold' }}>{task.task}</Box>
              <Box sx={{ color: 'text.secondary', fontSize: '0.875rem' }}>
                {task.taskType} - {task.timeStart}
              </Box>
            </Box>
          ))}
        </Box>
      )}
    </Box>
  );
};

export default Component4;
