import React, { useState, useEffect } from "react";
import { 
  TextField, 
  Button, 
  Grid, 
  Typography, 
  Box,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  DialogTitle,
  DialogContent,
  DialogActions
} from "@mui/material";
import axios from 'axios';

const GroupSubmit = ({ onSubmit, onClose, groupId }) => {
  const [formData, setFormData] = useState({
    taskName: "",
    description: "",
    timeBegin: "",
    timeEnd: "",
    dateBegin: "",
    dateEnd: "",
    assignTo: ""
  });
  const [members, setMembers] = useState([]);

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const response = await axios.get(
          `http://localhost:3000/api/groups/${groupId}/members`,
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        setMembers(response.data.members);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    fetchMembers();
  }, [groupId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <>
      <DialogTitle>Create New Task</DialogTitle>
      <DialogContent>
        <Box sx={{ mt: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Task Name"
                name="taskName"
                value={formData.taskName}
                onChange={handleChange}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
                multiline
                rows={4}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Time Begin"
                name="timeBegin"
                type="time"
                value={formData.timeBegin}
                onChange={handleChange}
                required
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Time End"
                name="timeEnd"
                type="time"
                value={formData.timeEnd}
                onChange={handleChange}
                required
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Date Begin"
                name="dateBegin"
                type="date"
                value={formData.dateBegin}
                onChange={handleChange}
                required
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Date End"
                name="dateEnd"
                type="date"
                value={formData.dateEnd}
                onChange={handleChange}
                required
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Assign To</InputLabel>
                <Select
                  name="assignTo"
                  value={formData.assignTo}
                  onChange={handleChange}
                  required
                  label="Assign To"
                >
                  {members.map((member) => (
                    <MenuItem key={member.user_id} value={member.user_id}>
                      {member.user_name} ({member.user_id})
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit} variant="contained" color="primary">
          Create Task
        </Button>
      </DialogActions>
    </>
  );
};

export default GroupSubmit;
