import React from 'react';
import { Box, Typography } from '@mui/material';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'; // Import biểu tượng lịch

const Component2 = () => {
  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 2.3, p: 1 }}>
      {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
        <Box
          key={day}
          sx={{
            display: 'flex',
            flexDirection: 'row', // Đặt hàng ngang cho ngày và biểu tượng
            alignItems: 'center',
            gap: 1,
          }}
        >
          <Box
            sx={{
              width: 40,
              height: 40,
              borderRadius: '50%',
              bgcolor: '#f5f5f5',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Typography>{day}</Typography>
          </Box>
          {day === 'Sun' && (
            <Box
              sx={{
                width: 35,
                height: 35,
                borderRadius: '39%', // bo góc
                bgcolor: '#1976d2',
                color: '#fff',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <CalendarMonthIcon />
            </Box>
          )}
        </Box>
      ))}
    </Box>
  );
};

export default Component2;
