import React, { useState } from 'react';
import { Box, Grid, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import TaskList from './TaskList'; // Import TaskList Component

const Component3 = () => {
  const items = ['Học Tập', 'Công Việc', 'Gia Đình', 'Hàng Ngày', 'Hàng Tháng', 'Hàng Năm'];

  // State để điều khiển việc mở/đóng Dialog
  const [open, setOpen] = useState(false);

  // Hàm mở dialog
  const handleClickOpen = () => {
    setOpen(true);
  }; 

  // Hàm đóng dialog
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Box sx={{ 
      flex: 1, // Chiếm hết không gian còn lại
      width: '100%',
      p: 2,
      marginRight: '-100px', // Mở rộng sang phải
    }}>
      <Grid
        container
        spacing={4}
        sx={{
          display: 'flex',
          alignItems: 'stretch',
          justifyContent: 'center',
          width: 'calc(100% + 100px)', // Bù đắp cho marginRight
          margin: '0',
        }}
      >
        {items.map((item, index) => (
          <Grid item xs={12} sm={6} md={4} key={index} sx={{
            padding: '16px', // Tăng padding cho Grid item
          }}>
            <Box
              onClick={handleClickOpen}
              sx={{
                height: '280px',
                width: '100%', // Đảm bảo box chiếm toàn bộ không gian của Grid item
                bgcolor: '#f5f5f5',
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '32px',
                fontWeight: 'bold',
                color: '#1976d2',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  bgcolor: '#e3f2fd',
                  boxShadow: '0 6px 12px rgba(0,0,0,0.15)',
                }
              }}
            >
              {item}
            </Box>
          </Grid>
        ))}
      </Grid>

      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth={false} // Tắt giới hạn chiều rộng mặc định
      >
        <DialogTitle>Danh Sách Nhiệm Vụ</DialogTitle>
        <DialogContent
          sx={{
            width: 900, // Chiều rộng cố định của Dialog
            height: 600, // Chiều cao cố định của Dialog
            overflowY: 'auto', // Cho phép cuộn dọc khi nội dung vượt quá
          }}
        >
          <TaskList />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Đóng</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Component3;
