import React, { useState } from 'react';
import { Box, Typography, Dialog, DialogTitle, DialogContent } from '@mui/material';
import Calendar from './Calendar'; // Giả sử bạn đã tạo Calendar component như trên

const Component2 = () => {
  const [openDialog, setOpenDialog] = useState(false); // State để mở/đóng Dialog

  const handleDialogOpen = () => {
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 3, p: 1 }}>
      {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => (
        <Box
          key={day}
          sx={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            gap: 2.5,
          }}
        >
          <Box
            sx={{
              width: 45,
              height: 45,
              borderRadius: '50%',
              bgcolor: '#f5f5f5',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Typography>{day}</Typography>
          </Box>
          {(index >= 1 && index <= 5) && ( // Hiển thị nền tròn cho thứ 2 đến thứ 6
            <Box
              sx={{
                width: 38,
                height: 38,
                borderRadius: '50%', // Đảm bảo là hình tròn
                bgcolor: '#1976d2',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
              onClick={handleDialogOpen} // Mở dialog khi nhấn vào nền tròn
            >
              {/* Không cần biểu tượng lịch ở đây */}
            </Box>
          )}
          {day === 'Sun' && ( // Giữ nguyên ô cho Chủ nhật
            <Box
              sx={{
                width: 38,
                height: 38,
                borderRadius: '50%', // Đảm bảo là hình tròn
                bgcolor: '#1976d2',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
              onClick={handleDialogOpen} // Mở dialog khi nhấn vào Chủ nhật
            >
              {/* Không cần biểu tượng lịch ở đây */}
            </Box>
          )}
        </Box>
      ))}

      {/* Hộp thoại hiển thị Calendar */}
      <Dialog open={openDialog} onClose={handleDialogClose} maxWidth="md" fullWidth>
        <DialogTitle>Chọn Ngày</DialogTitle>
        <DialogContent>
          <Calendar /> {/* Thêm component Calendar của bạn vào đây */}
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default Component2;
