import React, { useState, useEffect } from "react";
import {
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  List,
  ListItem,
  ListItemText,
  Divider,
  CircularProgress
} from "@mui/material";
import SendIcon from '@mui/icons-material/Send';
import axios from 'axios';

const ChatTab = ({ groupId }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMessages();
    // Thiết lập interval để fetch tin nhắn mới mỗi 5 giây
    const interval = setInterval(fetchMessages, 5000);
    return () => clearInterval(interval);
  }, [groupId]);

  const fetchMessages = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.get(
        `http://localhost:3000/api/groups/${groupId}/chat`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setMessages(response.data.messages);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching messages:', error);
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const token = localStorage.getItem('authToken');
      await axios.post(
        `http://localhost:3000/api/groups/${groupId}/chat`,
        { message: newMessage },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      setNewMessage("");
      fetchMessages(); // Fetch lại tin nhắn sau khi gửi
    } catch (error) {
      console.error('Error sending message:', error);
      alert('Không thể gửi tin nhắn. Vui lòng thử lại!');
    }
  };

  const formatTime = (date, time) => {
    const formattedDate = new Date(date).toLocaleDateString('vi-VN');
    return `${formattedDate} ${time.substring(0, 5)}`;
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ 
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      backgroundColor: '#f5f5f5',
      border: '1px solid #e0e0e0',
      borderRadius: '10px',
      overflow: 'hidden'
    }}>
      {/* Khu vực hiển thị tin nhắn */}
      <Box sx={{ 
        flex: 1,
        overflowY: 'auto',
        p: 2,
        backgroundColor: '#fff',
        '&::-webkit-scrollbar': {
          width: '8px'
        },
        '&::-webkit-scrollbar-track': {
          backgroundColor: '#f5f5f5'
        },
        '&::-webkit-scrollbar-thumb': {
          backgroundColor: '#888',
          borderRadius: '4px'
        }
      }}>
        <List>
          {messages.length === 0 ? (
            <Typography align="center" color="textSecondary">
              Chưa có tin nhắn nào
            </Typography>
          ) : (
            messages.map((msg) => (
              <React.Fragment key={msg.chat_id}>
                <ListItem alignItems="flex-start">
                  <ListItemText
                    primary={
                      <Typography
                        component="span"
                        variant="body1"
                        color="text.primary"
                        sx={{ fontWeight: 'bold' }}
                      >
                        {msg.user_name}
                      </Typography>
                    }
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          color="text.primary"
                        >
                          {msg.note}
                        </Typography>
                        <Typography
                          component="span"
                          variant="caption"
                          color="text.secondary"
                          sx={{ display: 'block', mt: 0.5 }}
                        >
                          {formatTime(msg.chat_date, msg.chat_time)}
                        </Typography>
                      </React.Fragment>
                    }
                  />
                </ListItem>
                <Divider component="li" />
              </React.Fragment>
            ))
          )}
        </List>
      </Box>

      {/* Khu vực nhập tin nhắn */}
      <Box sx={{ 
        p: 2,
        backgroundColor: '#fff',
        borderTop: '1px solid #e0e0e0',
        display: 'flex',
        gap: 1,
        alignItems: 'center'
      }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Nhập tin nhắn..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
          size="small"
          sx={{
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              borderRadius: '4px'
            }
          }}
        />
        <Button
          variant="contained"
          endIcon={<SendIcon />}
          onClick={handleSendMessage}
          disabled={!newMessage.trim()}
          sx={{
            borderRadius: '4px',
            minWidth: '100px'
          }}
        >
          Gửi
        </Button>
      </Box>
    </Box>
  );
};

export default ChatTab;
