import React, { useState } from "react";
import { Box, Tabs, Tab, Container, Paper } from "@mui/material";
import InfoTab from "./group/InfoTab";
import MembersTab from "./group/MembersTab";
import TasksTab from "./group/TasksTab";

const GroupMenu = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleChangeTab = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Paper elevation={3}>
        <Box>
          <Tabs value={activeTab} onChange={handleChangeTab} indicatorColor="primary" textColor="primary">
            <Tab label="Info" />
            <Tab label="Members" />
            <Tab label="Tasks" />
          </Tabs>

          <Box p={3}>
            {activeTab === 0 && <InfoTab />}
            {activeTab === 1 && <MembersTab />}
            {activeTab === 2 && <TasksTab />}
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default GroupMenu;
